#include "script::lib_script_common.js";
#include "script::Grid_Script.js"

var strSeparator = "	";

function form_OnLoadCompleted(obj)
{
	var z=0;
	if(length(trim(MainGrid.ed_JPSNO1.Text)) > 0){
		ds_SLTZ100D_BOX_SQ.InsertRow(z);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO1.Text);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXQYB","0");
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXAMB","0");
		z=z+1;
	}
	if(length(trim(MainGrid.ed_JPSNO2.Text)) > 0){
		ds_SLTZ100D_BOX_SQ.InsertRow(z);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO2.Text);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXQYB","0");
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXAMB","0");
		z=z+1;
	}
	if(length(trim(MainGrid.ed_JPSNO3.Text)) > 0){
		ds_SLTZ100D_BOX_SQ.InsertRow(z);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO3.Text);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXQYB","0");
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXAMB","0");
		z=z+1;
	}
	if(length(trim(MainGrid.ed_JPSNO4.Text)) > 0){
		ds_SLTZ100D_BOX_SQ.InsertRow(z);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO4.Text);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXQYB","0");
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXAMB","0");
		z=z+1;
	}
	if(length(trim(MainGrid.ed_JPSNO5.Text)) > 0){
		ds_SLTZ100D_BOX_SQ.InsertRow(z);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO5.Text);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXQYB","0");
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXAMB","0");
		z=z+1;
	}
	if(length(trim(MainGrid.ed_JPSNO6.Text)) > 0){
		ds_SLTZ100D_BOX_SQ.InsertRow(z);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO6.Text);
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXQYB","0");
		ds_SLTZ100D_BOX_SQ.SetColumn(z,"BOXAMB","0");
		z=z+1;
	}
	//trace(ds_SLTZ100D_BOX_SQ.SaveXML());
	http.Sync = true;
	var sKind		= "getSLTZ110";						
	var sMethodName = "service::pssallib/selectSLTZ100_BOX1.do";
	var sInDataSet  = "";
	var sOutDataSet = "ds_SLTZ100D_BOX=ds_SLTZ100D_BOX";
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " PYVND=" + quote(MainGrid.ed_pyvnd.Text);
	sArgument += " INOUT=" + quote(ca_INOUT.Value);
	sArgument += " MKLOC=" + quote(MainGrid.ed_MKLOC.Text );
	sArgument += " JPSNO1=" + quote(MainGrid.ed_JPSNO1.Text);
	sArgument += " JPSNO2=" + quote(MainGrid.ed_JPSNO2.Text);
	sArgument += " JPSNO3=" + quote(MainGrid.ed_JPSNO3.Text);
	sArgument += " JPSNO4=" + quote(MainGrid.ed_JPSNO4.Text);
	sArgument += " JPSNO5=" + quote(MainGrid.ed_JPSNO5.Text);
	sArgument += " JPSNO6=" + quote(MainGrid.ed_JPSNO6.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	var jpsno;
	for (var i=0; i<ds_SLTZ100D_BOX_SQ.GetRowCount(); i++){
		jpsno=toNumber(ds_SLTZ100D_BOX_SQ.GetColumn(i,"JPSNOB"));
		for(var j=0; j<ds_SLTZ100D_BOX.GetRowCount(); j++){
			if(jpsno == toNumber(ds_SLTZ100D_BOX.GetColumn(j,"JPSNOB"))){
				ds_SLTZ100D_BOX_SQ.SetColumn(i,"BOXQYB",ds_SLTZ100D_BOX.GetColumn(j,"BOXQYB"));
				ds_SLTZ100D_BOX_SQ.SetColumn(i,"BOXAMB",ds_SLTZ100D_BOX.GetColumn(j,"BOXAMB"));
				break;
			}
		}
	}
}

function btnConf_OnClick(obj)
{
	close(ds_SLTZ100D_BOX_SQ.SaveXML());
}
