/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 
	MainGrid.UserData = MainGrid.Height;

}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");		
}

function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	cb_searchCodeName1.Value = comps;
}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSLTZ101(){
	if(length(trim(st_pyvnd.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		ed_pyvnd.SetFocus();
		return;
	}	

	//조회
	var sKind		= "getSLTZ101";						
	var sMethodName = "service::pssallib/selectSLTZ101.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SLTZ101=ds_SLTZ101";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@PYVND=" + quote(ed_pyvnd.Text);
	sArgument += "@LNTNO=" + quote(ed_LNTNO.Text);
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}


}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setSLTZ101(){

	if(length(trim(st_pyvnd.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		ed_pyvnd.SetFocus();
		return;
	}	
	if(length(trim(displayDiv.ed_CARNO.Text)) < 1){ //차량번호
		alert(setPopMessage("4018"));  // [확인] 차량번호를 입력바랍니다.
		setMessage("4018");	
		displayDiv.ed_CARNO.SetFocus();
		return;
	}
	if(length(trim(displayDiv.ed_CFMCD.Text)) < 1){ //확인자코드
		alert(setPopMessage("4019"));  //[확인] 확인자코드를 확인바랍니다.
		setMessage("4019");	
		displayDiv.ed_CFMCD.SetFocus();
		return;
	}

	ds_SLTZ101_BOX.ClearData();

	if(displayDiv.MaskEdit4.Value == "N"){ //차량미처리
		var retVal;
		retVal = dialog("PSSALLIB::SLTZ101D.xml", "", 300,300, "Title=true CloseFlag=false");
		ds_SLTZ101_BOX.LoadXML(retVal);
	}else if(displayDiv.MaskEdit4.Value == "Y"){  //차량처리
		var rRow;
		rRow=ds_SLTZ101_BOX.InsertRow();
		ds_SLTZ101_BOX.SetColumn(rRow,"INOUTB","1");
		ds_SLTZ101_BOX.SetColumn(rRow,"JPSNOB","L000001");
		ds_SLTZ101_BOX.SetColumn(rRow,"BOXQYB","0");
		ds_SLTZ101_BOX.SetColumn(rRow,"BOXAMB","0");
		if(length(trim(displayDiv.ed_JPSNO1.Text)) > 0){
			rRow=ds_SLTZ101_BOX.InsertRow();
			ds_SLTZ101_BOX.SetColumn(rRow,"INOUTB","2");
			ds_SLTZ101_BOX.SetColumn(rRow,"JPSNOB",displayDiv.ed_JPSNO1.Text);
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXQYB","0");
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXAMB","0");
		}
		if(length(trim(displayDiv.ed_JPSNO2.Text)) > 0){
			rRow=ds_SLTZ101_BOX.InsertRow();
			ds_SLTZ101_BOX.SetColumn(rRow,"INOUTB","2");
			ds_SLTZ101_BOX.SetColumn(rRow,"JPSNOB",displayDiv.ed_JPSNO2.Text);
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXQYB","0");
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXAMB","0");
		}			
		if(length(trim(displayDiv.ed_JPSNO3.Text)) > 0){
			rRow=ds_SLTZ101_BOX.InsertRow();
			ds_SLTZ101_BOX.SetColumn(rRow,"INOUTB","2");
			ds_SLTZ101_BOX.SetColumn(rRow,"JPSNOB",displayDiv.ed_JPSNO3.Text);
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXQYB","0");
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXAMB","0");
		}
		if(length(trim(displayDiv.ed_JPSNO4.Text)) > 0){
			rRow=ds_SLTZ101_BOX.InsertRow();
			ds_SLTZ101_BOX.SetColumn(rRow,"INOUTB","2");
			ds_SLTZ101_BOX.SetColumn(rRow,"JPSNOB",displayDiv.ed_JPSNO4.Text);
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXQYB","0");
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXAMB","0");
		}
		if(length(trim(displayDiv.ed_JPSNO5.Text)) > 0){
			rRow=ds_SLTZ101_BOX.InsertRow();
			ds_SLTZ101_BOX.SetColumn(rRow,"INOUTB","2");
			ds_SLTZ101_BOX.SetColumn(rRow,"JPSNOB",displayDiv.ed_JPSNO5.Text);
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXQYB","0");
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXAMB","0");
		}
		if(length(trim(displayDiv.ed_JPSNO6.Text)) > 0){
			rRow=ds_SLTZ101_BOX.InsertRow();
			ds_SLTZ101_BOX.SetColumn(rRow,"INOUTB","2");
			ds_SLTZ101_BOX.SetColumn(rRow,"JPSNOB",displayDiv.ed_JPSNO6.Text);
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXQYB","0");
			ds_SLTZ101_BOX.SetColumn(rRow,"BOXAMB","0");
		}
	}else{
		alert(setPopMessage("1002"));
		setMessage("1002");
		return;
	}



	var arrINOUTB = array();
	var arrJPSNOB = array();
	var arrBOXQYB = array();
	for (var i=0; i<ds_SLTZ101_BOX.GetRowCount(); i++){
		arrINOUTB[i] = ds_SLTZ101_BOX.GetColumn(i,"INOUTB");
		arrJPSNOB[i] = ds_SLTZ101_BOX.GetColumn(i,"JPSNOB");
		arrBOXQYB[i] = ds_SLTZ101_BOX.GetColumn(i,"BOXQYB");
		if(arrJPSNOB[i] != "L000001"){
			object("displayDiv.ed_BOXQY"+(i+1)).Text=arrBOXQYB[i];
		}else{
			displayDiv.ed_BOXQY7.Text = arrBOXQYB[i];
		}
	}
 	http.Sync = true;
	var sKind		= "SAVE_SLTZ101";						
	var sMethodName = "service::pssallib/insertSLTZ101.do";
	var sInDataSet  = "";
	var sOutDataSet = "ds_SLTZ101_CHECK=ds_SLTZ101_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(displayDiv.med_COMPS.Value);
	sArgument += " INOUT=" + quote(displayDiv.MaskEdit5.Value);
	sArgument += " EXPCD=" + quote(displayDiv.MaskEdit4.Value);
	sArgument += " LNTNO=" + quote(trim(displayDiv.med_LNTNO.Value));
	sArgument += " CARNO=" + quote(trim(displayDiv.ed_CARNO.Text));
	sArgument += " CFMCD=" + quote(trim(displayDiv.ed_CFMCD.Text));
	sArgument += " CFMDT=" + quote(displayDiv.ca_CFMDT.Value);

	sArgument += " INOUTB1=" + quote(arrINOUTB[0]);
	sArgument += " INOUTB2=" + quote(arrINOUTB[1]);
	sArgument += " INOUTB3=" + quote(arrINOUTB[2]);
	sArgument += " INOUTB4=" + quote(arrINOUTB[3]);
	sArgument += " INOUTB5=" + quote(arrINOUTB[4]);
	sArgument += " INOUTB6=" + quote(arrINOUTB[5]);
	sArgument += " INOUTB7=" + quote(arrINOUTB[6]);
	sArgument += " JPSNOB1=" + quote(arrJPSNOB[0]);
	sArgument += " JPSNOB2=" + quote(arrJPSNOB[1]);
	sArgument += " JPSNOB3=" + quote(arrJPSNOB[2]);
	sArgument += " JPSNOB4=" + quote(arrJPSNOB[3]);
	sArgument += " JPSNOB5=" + quote(arrJPSNOB[4]);
	sArgument += " JPSNOB6=" + quote(arrJPSNOB[5]);
	sArgument += " JPSNOB7=" + quote(arrJPSNOB[6]);
	sArgument += " BOXQYB1=" + quote(arrBOXQYB[0]);
	sArgument += " BOXQYB2=" + quote(arrBOXQYB[1]);
	sArgument += " BOXQYB3=" + quote(arrBOXQYB[2]);
	sArgument += " BOXQYB4=" + quote(arrBOXQYB[3]);
	sArgument += " BOXQYB5=" + quote(arrBOXQYB[4]);
	sArgument += " BOXQYB6=" + quote(arrBOXQYB[5]);
	sArgument += " BOXQYB7=" + quote(arrBOXQYB[6]);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	ds_SLTZ101_BOX.ClearData();

 }

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "용차사용신청확인등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|용차사용신청확인등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	//trace(ds_SLTZ101_CHECK.SaveXML());
	if (ds_SLTZ101_CHECK.GetRowCount() > 0){			
		for(var i=0;i<ds_SLTZ101_CHECK.GetRowCount();i++){		
			alert(ds_SLTZ101_CHECK.GetColumn(i,"LNTNO") + " ="
			    + ds_SLTZ101_CHECK.GetColumn(i,"RTMSG"));	
		}
		getSLTZ101();        //error항목이 존재시에는 재조회		
		return;		
	}	
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//setSLTZ101_110();
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

