function imgMaker_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_pyvnd.Text);
	if (length(trim(ed_pyvnd.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "B0";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_pyvnd.Text = rtn[0];
		st_pyvnd.Text = rtn[1];
	}
}

function ed_pyvnd_OnCharChanged(obj,strPreText,strPostText)
{
	st_pyvnd.Text = "";
}

function ed_pyvnd_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_pyvnd.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CUSMAS1.GetRowCount() < 1){
		alert(setPopMessage("1002"));
		setMessage("1002");	
		ed_pyvnd.SetFocus();
		return;
	}
	st_pyvnd.Text=ds_CUSMAS1.GetColumn(0,"CUSNM");
	http.usewaitcursor = true;
}


function ds_SLTZ101_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "CFMCD"){
		//check
		http.Sync = true;
		var sKind		= "getmst050pf";		
		var sMethodName = "service::pscomlib/selectMST050PF.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST050PF=ds_MST050PF";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
			sArgument  += " GRPCD=" + quote("SAL");
			sArgument  += " MSTCD=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MST050PF.GetRowCount() < 1){
			alert(setPopMessage("4019"));
			setMessage("4019");	
			return false;
		}
	}
}

function ds_SLTZ101_OnRowPosChanged(obj,nOldRow,nRow)
{
	if(ds_SLTZ101.GetRowCount() < 1) return;
	clear_Data();
	http.Sync 	= true;
	var sKind		= "getsltz101_a";						
	var sMethodName = "service::pssallib/selectSLTZ101_A.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SLTZ101_A=ds_SLTZ101_A";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " PYVND=" + quote(ds_SLTZ101.GetColumn(nRow,"PYVND"));
		sArgument += " MKLOC=" + quote(ds_SLTZ101.GetColumn(nRow,"MKLOC"));
		sArgument += " LNTNO=" + quote(ds_SLTZ101.GetColumn(nRow,"LNTNO"));
		//sArgument += " INOUT=" + quote(ds_SLTZ101.GetColumn(nRow,"INOUT"));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if(ds_SLTZ101_A.GetRowCount() > 0){
		var arrValueJ = array();
		var arrValueQ = array();
		var arrValueA = array();
		for(var i=0; i<ds_SLTZ101_A.GetRowCount();i++){
			if (ds_SLTZ101_A.GetColumn(i,"JPSNO") != "L000001"){
				arrValueJ[i] = ds_SLTZ101_A.GetColumn(i,"JPSNO");
				arrValueQ[i] = ds_SLTZ101_A.GetColumn(i,"BOXQY");
				arrValueA[i] = ds_SLTZ101_A.GetColumn(i,"BOXAM");
			}else{
				arrValueJ[6] = ds_SLTZ101_A.GetColumn(i,"JPSNO");
				arrValueQ[6] = ds_SLTZ101_A.GetColumn(i,"BOXQY");
				arrValueA[6] = ds_SLTZ101_A.GetColumn(i,"BOXAM");
			}
		}
		displayDiv.ed_JPSNO1.Text = arrValueJ[0];
		displayDiv.ed_JPSNO2.Text = arrValueJ[1];
		displayDiv.ed_JPSNO3.Text = arrValueJ[2];
		displayDiv.ed_JPSNO4.Text = arrValueJ[3];
		displayDiv.ed_JPSNO5.Text = arrValueJ[4];
		displayDiv.ed_JPSNO6.Text = arrValueJ[5];
		displayDiv.ed_BOXQY1.Text = arrValueQ[0];
		displayDiv.ed_BOXQY2.Text = arrValueQ[1];
		displayDiv.ed_BOXQY3.Text = arrValueQ[2];
		displayDiv.ed_BOXQY4.Text = arrValueQ[3];
		displayDiv.ed_BOXQY5.Text = arrValueQ[4];
		displayDiv.ed_BOXQY6.Text = arrValueQ[5];
		displayDiv.ed_BOXCT1.Text = arrValueA[0];
		displayDiv.ed_BOXCT2.Text = arrValueA[1];
		displayDiv.ed_BOXCT3.Text = arrValueA[2];
		displayDiv.ed_BOXCT4.Text = arrValueA[3];
		displayDiv.ed_BOXCT5.Text = arrValueA[4];
		displayDiv.ed_BOXCT6.Text = arrValueA[5];
		if(length(arrValueA[6]) < 1){
			displayDiv.ed_JPSNO7.Text = "L000001";
			displayDiv.ed_BOXQY7.Text = "0";
			displayDiv.ed_BOXCT7.Text = "0";
		}else{
			displayDiv.ed_JPSNO7.Text = arrValueJ[6];
			displayDiv.ed_BOXQY7.Text = arrValueQ[6];
			displayDiv.ed_BOXCT7.Text = arrValueA[6];
		}
	}
	if(ds_SLTZ101.GetColumn(nRow,"MGMGU") == "*"){
		displayDiv.ed_CARNO.Enable = false;
		displayDiv.ed_CFMCD.Enable = false;
		displayDiv.ca_CFMDT.Enable = false;
		displayDiv.btnConf.Enable = false;
	}else{
		displayDiv.ed_CARNO.Enable = true;
		displayDiv.ed_CFMCD.Enable = true;
		displayDiv.ca_CFMDT.Enable = true;
		displayDiv.btnConf.Enable = true;
	}

}

