{
	displayDiv.ed_JPSNO1.Text = "";
	displayDiv.ed_JPSNO2.Text = "";
	displayDiv.ed_JPSNO3.Text = "";
	displayDiv.ed_JPSNO4.Text = "";
	displayDiv.ed_JPSNO5.Text = "";
	displayDiv.ed_JPSNO6.Text = "";
	displayDiv.ed_JPSNO7.Text = "";
	displayDiv.ed_BOXQY1.Text = "";
	displayDiv.ed_BOXQY2.Text = "";
	displayDiv.ed_BOXQY3.Text = "";
	displayDiv.ed_BOXQY4.Text = "";
	displayDiv.ed_BOXQY5.Text = "";
	displayDiv.ed_BOXQY6.Text = "";
	displayDiv.ed_BOXQY7.Text = "";
	displayDiv.ed_BOXCT1.Text = "";
	displayDiv.ed_BOXCT2.Text = "";
	displayDiv.ed_BOXCT3.Text = "";
	displayDiv.ed_BOXCT4.Text = "";
	displayDiv.ed_BOXCT5.Text = "";
	displayDiv.ed_BOXCT6.Text = "";
	displayDiv.ed_BOXCT7.Text = "";
}
