{
	ds_SLTZ104Q.ClearData();
}

function cb_ad_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	if(strCode=="B"){
		st_MGMGB.Text = "확 인 일 자";
		ca_MGMYM.Visible = false;
		Static3.Visible = false;
		ca_CFMDTF.Visible = true;
		ca_CFMDTT.Visible = true;
		Static8.Visible = true;
		Static9.Visible = true;
	}else if(strCode=="A"){
		st_MGMGB.Text = "마 감 년 월";
		ca_MGMYM.Visible = true;
		Static3.Visible = true;
		ca_CFMDTF.Visible = false;
		ca_CFMDTT.Visible = false;
		Static8.Visible = false;
		Static9.Visible = false;
	}
	ds_SLTZ104Q.ClearData();
}
