function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 발행
 * 주요변수 : 
 **********************************************************************/ 
 function prtSLTZ113R()
 {
	if(length(ed_LNTNO_F.Text)<1){
		alert(setPopMessage("4004"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("4004");	
		return;
	}
	if(length(ed_LNTNO_T.Text)<1){
		alert(setPopMessage("4004"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("4004");	
		return;
	}
	http.Sync 	= true;
	var sKind		= "getSLTZ113R";						
	var sMethodName = "service::pssallib/selectSLTZ113R.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SLTZ113R=ds_SLTZ113R";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " LNTNO_F=" + quote(ed_LNTNO_F.Text);
	sArgument += " LNTNO_T=" + quote(ed_LNTNO_T.Text);
	//sArgument += " GROUP=" + quote(ComboBoxClass.Value);      
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync	= false;

	ubi0.setJrfFileDir(G_baseURL+"/ui/PSSALLIB/REPORT/");
	//C:\eclipse\workspace\DENSO\WebContent
	//ubi0.setJrfFileDir("C:/eclipse/workspace/DENSO/WebContent"+"/ui/PSSALLIB/REPORT/");
	ubi0.setJrfFileName("SLTZ113R.jrf");
	ubi0.SetDataset("ds_SLTZ113R", "ds_SLTZ113R");	
//	ubi0.SetArgument("DHCODE", search_dhcode.Text+"("+search_dhcode.Value+")");
//	ubi0.SetArgument("WRKDT", parent.search_wrkdt.Text);	
//	ubi0.SetArgument("CURRENT_TIME", substr(Getdate(),8,2)+":"+substr(Getdate(),10,2)+":"+substr(Getdate(),12,2));
	ubi0.Retrieve();
}
