function setExcel()
{
	var fName   = substr(ca_plnym.Value,0,6)+"월판매생산계획대실적LIST";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월판매생산계획대실적LIST";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
