var nowDate = today();


/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();	
	getComCode();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 	
	ca_RVPDT_F.Value = nowDate;
	ca_RVPDT_T.Value = nowDate;
	MainGrid.UserData = MainGrid.Height;
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

}

function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	//ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	cb_searchCodeName1.Value = comps;

}
/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSMBZ011Q()
{
	var p_DATE1,p_DATE2,p_pyvnd1,p_pyvnd2,p_bnk1,p_bnk2;
	p_DATE1 = iif (length(trim(ca_RVPDT_F.Value)) == 0, '0', trim(ca_RVPDT_F.Value));
	p_DATE2 = iif (length(trim(ca_RVPDT_T.Value)) == 0 , '99999999', trim(ca_RVPDT_T.Value));
	p_pyvnd1 = iif (length(trim(ed_RVMAK_F.Value)) == 0, '0', trim(ed_RVMAK_F.Value));
	p_pyvnd2 = iif (length(trim(ed_RVBNK_T.Value)) == 0 , '99999999', trim(ed_RVBNK_T.Value));
	p_bnk1 = iif (length(trim(ed_RVBNK_F.Value)) == 0 , '0', trim(ed_RVBNK_F.Value));
	p_bnk2 = iif (length(trim(ed_RVMAK_T.Value)) == 0 , '999999', trim(ed_RVMAK_T.Value));

	var sKind		= "getSMBZ011Q";					
	var sMethodName = "service::pssallib/selectSMBZ011Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SMBZ011Q=ds_SMBZ011Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@RVPDT_F=" + quote(p_DATE1); //만기일자 from 
	sArgument += "@RVPDT_T=" + quote(p_DATE2); //만기일자 to
	sArgument += "@RVBNK_F=" + quote(p_bnk1); //지급은행 from 
	sArgument += "@RVBNK_T=" + quote(p_bnk2); //지급은행 to
	sArgument += "@RVMAK_F=" + quote(p_pyvnd1); //발행처 from 
	sArgument += "@RVMAK_T=" + quote(p_pyvnd2); //발행처 to
	sArgument += "@GUB=" + quote(rdo_gub.Value); 
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //[알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "어음입금상세조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|어음입금상세조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	// Ctrl + V
	/*} else if(bCtrl == 1 && nChar == 86) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardPaste(obj, strSeparator);

	// Ctrl + Z
	} else if(bCtrl == 1 && nChar == 90) {

		// grd_Standard.AreaSelect = true;
		grd_fn_CellReset(obj);*/
	}
}

