function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	gFcOnTopButtonColor("2.1"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 

	MainGrid.UserData = MainGrid.Height;
	getMST040PF();
	MainDiv.Enable = false;
	MainDiv.ed_PYVND.Enable = false;
	MainDiv.ed_SPKNM.Enable = false;
	MainDiv.ca_SIPDT.Enable = false;
	MainDiv.ca_SPKDT.Enable = false;
	ed_SIPNO.SetFocus();
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");


	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");		
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	cb_searchCodeName1.Value = comps;
}

function getMST040PF(){
	http.Sync 	= true;
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " MAJCD=" + quote("SIPNO");		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST040PF.rowcount != 0){
		st_BSIPNO.Text = ds_MST040PF.GetColumn(0,"MJCHR")+"  "+right("0000"+ds_MST040PF.GetColumn(0,"MJINT"),4);
		ed_SIPNO.Text = toNumber(ds_MST040PF.GetColumn(0,"MJINT"))+1;
	}else{
		alert(setPopMessage("1444"));  // [경고] 시스템에 문제가 발생하였습니다. 디오네 종료 후 재가동바랍니다.
		setMessage("1444");	
	}
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSMBZ100(){

	if(length(trim(ed_SIPNO.Text)) < 1){
		alert(setPopMessage("4035"));  //[[확인] 수금번호를 입력바랍니다.
		setMessage("4035");	
		ed_SIPNO.SetFocus();
		return;
	}	
	var cat_SIPNO =  left(st_BSIPNO.Text,4) + right("0000"+ed_SIPNO.Text,4);
	//조회
	var sKind		= "getSMBZ100";						
	var sMethodName = "service::pssallib/selectSMBZ100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SMBZ100=ds_SMBZ100";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@SIPNO=" + quote(cat_SIPNO);

	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	

	if (str == 'T'){  //정상조회
		MainDiv.ed_PYVND.Text =  ds_SMBZ100.GetColumn(0,"PYVND");
		MainDiv.ed_SPKNM.Text =  trim(ds_SMBZ100.GetColumn(0,"SPKNM"));
		MainDiv.ca_SIPDT.Value =  ds_SMBZ100.GetColumn(0,"SIPDT");
		MainDiv.ca_SPKDT.Value =  ds_SMBZ100.GetColumn(0,"SPKDT");
		MainDiv.st_pyvnd.Text =  ds_SMBZ100.GetColumn(0,"CUSNM");
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //   [알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	ed_SIPNO.SetFocus();
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setSMBZ100(){

	if(length(trim(MainDiv.ed_PYVND.Value)) < 1){
		alert(setPopMessage("4007"));  //  
		setMessage("4007");
		return;
	}

	if(length(trim(MainDiv.ed_SPKNM.Value)) < 1){
		alert(setPopMessage("4050"));  //  [확인] 입금자명을 입력바랍니다.
		setMessage("4050");
		return;
	}

	if(length(trim(MainDiv.ca_SIPDT.Value)) < 1){
		alert(setPopMessage("4041"));  //  [확인] 수금일자를 확인바랍니다.
		setMessage("4041");
		return;
	}

	if(length(trim(MainDiv.ca_SPKDT.Value)) < 1){
		alert(setPopMessage("4051"));  //  [확인] 입금일자를 확인바랍니다.
		setMessage("4051");
		return;
	}
	ds_SMBZ100.fireEvent = false;
	ds_SMBZ100.UpdateControl = false;
	for(i=0;i<ds_SMBZ100.rowcount;i++){
		var c_SIPCD = ds_SMBZ100.GetColumn(i,"SIPCD");
		var c_SDCNO = ds_SMBZ100.GetColumn(i,"SDCNO");
		var c_SIPAM = ds_SMBZ100.GetColumn(i,"SIPAM");

		if (c_SIPCD == null || c_SIPCD == ""){
			ds_SMBZ100.SetRowType(i,"normal");
		}else{
			if(c_SIPCD == "10"){
				if(!check_SIPCD10(c_SDCNO,c_SIPAM)){
					alert(setPopMessage("4049")); // [확인] 어음입금 마스타의 어음금액과 일치하지 않습니다.
					setMessage("4049");
					ds_SMBZ100.fireEvent = true;
					mainGrid.SetFocus();
					ds_SMBZ100.rowposition = i;
					MainGrid.SetCellPos(3);
					return;
				}
			}else if(c_SIPAM == null || toNumber(c_SIPAM) == 0){
				alert(setPopMessage("4052"));  // [확인] 금액을 확인바랍니다.
				setMessage("4052");
				MainGrid.SetFocus();
				ds_SMBZ100.rowposition = i;
				MainGrid.SetCellPos(3);
				ds_SMBZ100.fireEvent = true;
				return;
			}else if(c_SDCNO == null){
				ds_SMBZ100.SetColumn(i,"SDCNO","0");
			}
		}

	}
	ds_SMBZ100.fireEvent = true;

	var sKind		= "SAVE_SMBZ100";						
	var sMethodName = "service::pssallib/insertSMBZ100.do";
	var sInDataSet  = "ds_SMBZ100=ds_SMBZ100:U";
	var sOutDataSet = "ds_SMBZ100_CHECK=ds_SMBZ100_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " MSIPNO=" + quote(ed_SIPNO.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

 }

/********************************************************************** 
 * 내용설명 : 수금코드10은 어음마스타의 어음금액과 일치해야함.
 * 주요변수 : 
**********************************************************************/ 
function check_SIPCD10(varNewValue1,varNewValue2)
{
	if (length(varNewValue1) < 1){
		return false;
	}
	http.Sync = true;
	var sKind		= "getsmb010pf";		
	var sMethodName = "service::pssallib/selectSMBZ100_010.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SMB010PF=ds_SMB010PF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument  += " RVBNO=" + quote(varNewValue1);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if (ds_SMB010PF.GetRowCount() < 1){	
		return false;
	}
	if (ds_SMB010PF.GetColumn(0,"RVBAM") != varNewValue2){
		return false;
	}
	return true;

}


