 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid| ";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "getSMBZ100"){
		alert(ds_SMBZ100_A.GetRowCount());
	}
	if (svcId = "SAVE_SMBZ100"){
		if (ds_SMBZ100_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_SMBZ100_CHECK.GetRowCount();i++){		
				alert(ds_SMBZ100_CHECK.GetColumn(i,"SIPNO") + " , "
				    + ds_SMBZ100_CHECK.GetColumn(i,"SEQNO") + " ="
					+ ds_SMBZ100_CHECK.GetColumn(i,"RTMSG"));	
			}
			//return;		
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else {
		if(toNumber(ed_SIPNO.Text) > toNumber(right(st_BSIPNO.Text,4))){
			setMst040pf(); //납품대금 수금번호 저장(MST040PF)
		}
		alert(setPopMessage("1001"));
		setMessage("1001");	
		//getSMBZ100();
		getMST040PF();
		MainDiv.Enable = false;
	}
}
/**********************************************************************
 * 내용설명 : 납품대금 수금번호 저장(MST040PF)
 **********************************************************************/
 function setMst040pf()
 {
	http.Sync = true;
	var sKind		= "setmst040";
	var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("SIPNO");
	sArgument += " MJINT=" + quote(ed_SIPNO.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

}
