function fn_Search()
{
	ed_Action.Text = "Q";
	getSMBZ100();
	MainDiv.Enable = false;
	MainDiv.ed_PYVND.Enable = false;
	MainDiv.ed_SPKNM.Enable = false;
	MainDiv.ca_SIPDT.Enable = false;
	MainDiv.ca_SPKDT.Enable = false;
}

function fn_Input()
{
	if(length(trim(ed_SIPNO.Text)) < 1){
		alert(setPopMessage("4035"));  //[확인] 수금번호를 확인바랍니다.
		setMessage("4035");	
		ed_SIPNO.SetFocus();
		return;
	}
	if(checkInsertCount("ds_SMBZ100")== false) return;

	getSMBZ100_DCHK();
	ed_Action.Text = "A";
	if (toNumber(ds_SMBZ100_DCHK.GetColumn(0,"CNT")) != 0){
		if(confirm("이미 등록된 자료입니다. 기등록된 자료에 추가하시겠습니까? ") == true){
			var cnt1 =ds_SMBZ100.rowcount;
			if(cnt1<1) getSMBZ100();  //조회->수정->추가클릭시에는 다시조회하지 않는다.
			var cnt2 =ds_SMBZ100.rowcount;
			ds_SMBZ100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
			var seqMaxNumber = toNumber(ds_SMBZ100_DCHK.GetColumn(0,"SEQ")); //마지막 일련번호 가져옴.
			var seqNumber = seqMaxNumber;
			for (var i=ds_SMBZ100.rowcount;i<18;i++){
				seqNumber = seqNumber+1;
				ds_SMBZ100.AddRow();
				ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
				ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "SIPNO", left(st_BSIPNO.Text,4) + right("0000"+ed_SIPNO.Text,4)); // default값 설정
				ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "PYVND", MainDiv.ed_PYVND.Text);// default값 설정 ----업체코드
				ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "SPKNM", MainDiv.ed_SPKNM.Text);// default값 설정 ----입금자명
				ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "SIPDT", MainDiv.ca_SIPDT.Value);// default값 설정----일자
				ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "SPKDT", MainDiv.ca_SPKDT.Value);// default값 설정----일자
				ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "SEQNO", seqNumber); // default값 설정
			}
			ds_SMBZ100.FireEvent=true;
			MainDiv.Enable = false;
			MainDiv.ed_PYVND.Enable = false;
			MainDiv.ed_SPKNM.Enable = false;
			MainDiv.ca_SIPDT.Enable = false;
			MainDiv.ca_SPKDT.Enable = false;
			MainGrid.SetFocus();
			ds_SMBZ100.rowposition = cnt2;
			MainGrid.SetCellPos(0);
		}else{
			//alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			//setMessage("1520");	
			ed_SIPNO.SetFocus();
			return;
		}
	}else{
		if(toNumber(ed_SIPNO.Text) != toNumber(right(st_BSIPNO.Text,4))+1){
			alert(setPopMessage("4035"));  //[확인] 수금번호를 확인바랍니다.
			setMessage("4035");	
			ed_SIPNO.SetFocus();
			return;
		}
		ds_SMBZ100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
		for (var i=0;i<18;i++){
			ds_SMBZ100.AddRow();
			ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
			ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "SIPNO", left(st_BSIPNO.Text,4) + right("0000"+ed_SIPNO.Text,4)); // default값 설정
			ds_SMBZ100.SetColumn(ds_SMBZ100.Row, "SEQNO", i+1); // default값 설정
		}
		ds_SMBZ100.rowposition = 0;
		ds_SMBZ100.FireEvent=true;	
		MainDiv.Enable = true;
		MainDiv.ed_PYVND.Enable = true;
		MainDiv.ed_SPKNM.Enable = true;
		MainDiv.ca_SIPDT.Enable = true;
		MainDiv.ca_SPKDT.Enable = true;
		MainDiv.ed_SPKNM.Text = G_ds_user.GetColumn(0,"USER_NAME");
		MainDiv.ed_PYVND.SetFocus();
	}


}

