		MainDiv.ed_pyvnd.Text = rtn[0];
		MainDiv.st_pyvnd.Text = rtn[1];
	}
}


function ds_SMBZ100_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;  //transaction호출후 저장이벤트가 발생되지 않아서...
	if (strColumnID == "SIPCD"){
		if (length(varNewValue) < 1) {
			alert(setPopMessage("4045"));  //[경고] 수금코드를 입력바랍니다.
			setMessage("4045");	
			return false;
		}
		//업체코드,수금일자,입금일자,입금자명 미등록시 진행불가
		if(length(trim(MainDiv.ed_PYVND.Value)) < 1){
			alert(setPopMessage("4007"));  //  
			setMessage("4007");
			return false;
		}
		if(length(trim(MainDiv.ed_SPKNM.Value)) < 1){
			alert(setPopMessage("4050"));  //  [확인] 입금자명을 입력바랍니다.
			setMessage("4050");
			return false;
		}
		if(length(trim(MainDiv.ca_SIPDT.Value)) < 1){
			alert(setPopMessage("4041"));  //  [확인] 수금일자를 확인바랍니다.
			setMessage("4041");
			return false;
		}
		if(length(trim(MainDiv.ca_SPKDT.Value)) < 1){
			alert(setPopMessage("4051"));  //  [확인] 입금일자를 확인바랍니다.
			setMessage("4051");
			return false;
		}
		http.Sync = true;
		var sKind		= "getmst050pf";		
		var sMethodName = "service::pscomlib/selectMST050PF.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST050PF=ds_MST050PF";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
			sArgument  += " GRPCD=" + quote("SIP");
			sArgument  += " MSTCD=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MST050PF.GetRowCount() < 1){
			alert(setPopMessage("4046"));  //[확인] 수금코드를 확인바랍니다.
			setMessage("4046");
			return false;
		}
		can_SIPNM = ds_MST050PF.GetColumn(0,"MSTRM");
	}
	if (strColumnID == "SDCNO"){        //어음번호
		var temp_sipcd = ds_SMBZ100.GetColumn(nRow,"SIPCD");
		if (length(temp_sipcd) < 1) {
			alert(setPopMessage("4045"));  //[경고] 수금코드를 입력바랍니다.
			setMessage("4045");
			return false;
		}
		if (temp_sipcd == "10"){
			if (length(varNewValue) < 1) {
				alert(setPopMessage("4047"));  //[확인] 어음번호를 입력바랍니다.
				setMessage("4047");	
				return false;
			}
			http.Sync = true;
			var sKind		= "getsmb010pf";		
			var sMethodName = "service::pssallib/selectSMBZ100_010.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_SMB010PF=ds_SMB010PF";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
				sArgument  += " RVBNO=" + quote(varNewValue);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_SMB010PF.GetRowCount() < 1){
				alert(setPopMessage("4044"));  //[확인] 어음입금 마스타에 미등록된 번호입니다.
				setMessage("4044");	
				return false;
			}
			can_RVBAM = ds_SMB010PF.GetColumn(0,"RVBAM");
			can_RVPDT = ds_SMB010PF.GetColumn(0,"RVPDT");
			can_RVMDT = ds_SMB010PF.GetColumn(0,"RVMDT");
		}
	}
	if (strColumnID == "SIPAM"){        //어음금액
		var temp_sipcd = ds_SMBZ100.GetColumn(nRow,"SIPCD");
		if (length(temp_sipcd) < 1) {
			alert(setPopMessage("4045"));  //[경고] 수금코드를 입력바랍니다.
			setMessage("4045");
			return false;
		}
		if (temp_sipcd == "10"){
			if (length(varNewValue) < 1) {
				alert(setPopMessage("4047"));  //[확인] 어음번호를 입력바랍니다.
				setMessage("4047");	
				return false;
			}
			if(toNumber(varNewValue) != toNumber(can_RVBAM)){
				alert(setPopMessage("4049"));  //[확인] 어음입금 마스타의 어음금액과 일치하지 않습니다.
				setMessage("4049");	
				return false;
			}
		}
		if(toNumber(varNewValue) != 0){
			can_SPKAM = convertHanjaAmt(varNewValue);
		}
	}

	http.usewaitcursor = true;
}

function ds_SMBZ100_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "SIPCD"){
		ds_SMBZ100.SetColumn(nRow,"MSTRM",can_SIPNM);
		ds_SMBZ100.SetColumn(nRow, "PYVND", MainDiv.ed_PYVND.Text);// default값 설정 ----업체코드
		ds_SMBZ100.SetColumn(nRow, "SPKNM", MainDiv.ed_SPKNM.Text);// default값 설정 ----입금자명
		ds_SMBZ100.SetColumn(nRow, "SIPDT", MainDiv.ca_SIPDT.Value);// default값 설정----일자
		ds_SMBZ100.SetColumn(nRow, "SPKDT", MainDiv.ca_SPKDT.Value);// default값 설정----일자
	}
	if (strColumnID == "SDCNO"){
		if (ds_SMBZ100.GetColumn(nRow,"SIPCD") == "10"){
			ds_SMBZ100.SetColumn(nRow,"RVPDT",can_RVPDT);
			ds_SMBZ100.SetColumn(nRow,"RVMDT",can_RVMDT);
			ds_SMBZ100.SetColumn(nRow,"SIPAM",can_RVBAM);
		}
	}
	if (strColumnID == "SIPAM"){
		ds_SMBZ100.SetColumn(nRow,"SPKAM",can_SPKAM);
	}
}

function MainDiv_ed_pyvnd_OnChanged(obj,strText)
{
	if(length(trim(strText)) == 4) { 
		http.Sync 	= true;
		var sKind		= "getCUSMAS";		
		var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " CUSNO=" + quote(strText);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_CUSMAS1.GetRowCount() < 1){
			alert(setPopMessage("4007"));
			setMessage("4007");	
			MainDiv.st_pyvnd.Text = "";
			MainDiv.ed_pyvnd.SetFocus();
			return;
		}
		MainDiv.st_pyvnd.Text = ds_CUSMAS1.GetColumn(0,"CUSNM");
	}
}



function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1016")); setMessage("1016");
		}
	}
}

function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		if(confirm("저장하시겠습니까? ") == false) return;
		fn_Save();
	}
}
