function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	//ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	cb_searchCodeName1.Value = comps;

}

function getMst040()
{
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("SMB120PFMAGAM");
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
	next_month = AddMonth(ds_MST040PF.GetColumn(0,"MJINT")+"01",1);
	cal_JSOYM.Value = next_month;
	//alert(next_month);
}
/********************************************************************** 
 * 내용설명 : 마감
 * 주요변수 : 
 **********************************************************************/ 
function setSMBZ120C(){
	if(confirm(substr(cal_JSOYM.Value,0,4)+"년 "+substr(cal_JSOYM.Value,4,2)+"월 마감작업입니다. 작업하시겠습니까? ") == false) return;
	getMst040();
	if(next_month != cal_JSOYM.Value) {
		alert("마감월 확인바랍니다.");
		return;
	}

	var jsoyy;
	if(toNumber(substr(cal_JSOYM.Value,4,2)) < 4){
		jsoyy = toNumber(substr(cal_JSOYM.Value,0,4)) - 1;
	}else{
		jsoyy = toNumber(substr(cal_JSOYM.Value,0,4));
	}
	var b_misxx,debxx,crexx,misxx;
	if(toNumber(substr(cal_JSOYM.Value,4,2)) == 4){
		b_misxx = "JBSAM";
	}else{
		b_misxx = "MIS"+substr(AddMonth(cal_JSOYM.Value,-1),4,2);
	}
	debxx="DEB"+substr(cal_JSOYM.Value,4,2);
	crexx="CRE"+substr(cal_JSOYM.Value,4,2);
	misxx="MIS"+substr(cal_JSOYM.Value,4,2);
	http.Sync = true;
	var sKind		= "getSMBZ120C";					
	var sMethodName = "service::pssallib/selectSMBZ120C.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SMBZ120C=ds_SMBZ120C";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	//sArgument += " PYVND=" + quote(trim(ed_pyvnd.Text)); //
	sArgument += " YYMM=" + quote(substr(cal_JSOYM.Value,0,6)); //선택년월
	sArgument += " YYYY=" + quote(jsoyy); //계산년
	sArgument += " B_MISMM=" + quote(b_misxx);
	//sArgument += " MM=" + quote(substr(cal_JSOYM.Value,4,2)); //선택월
	//sArgument += " YYYY=" + quote(jsoyy); //계산년
	//var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if (ds_SMBZ120C.rowcount < 1){
		alert("마감할 내역이 존재하지 않습니다.");
		return;
	}

	http.Sync = true;
	var sKind		= "setSMBZ120C";					
	var sMethodName = "service::pssallib/insertSMBZ120C.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SMBZ120C=ds_SMBZ120C:A";         				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SMBZ120C_CHECK=ds_SMBZ120C_CHECK"; 			// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	//sArgument += " PYVND=" + quote(trim(ed_pyvnd.Text)); //
	sArgument += " YYMM=" + quote(substr(cal_JSOYM.Value,0,6)); //선택년월
	sArgument += " MM=" + quote(substr(cal_JSOYM.Value,4,2)); //선택월
	sArgument += " YYYY=" + quote(jsoyy); //계산년
	sArgument += " DEBMM=" + quote(debxx);
	sArgument += " CREMM=" + quote(crexx);
	sArgument += " MISMM=" + quote(misxx);
	//var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "setSMBZ120C"){
		if (ds_SMBZ120C_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_SMBZ120C_CHECK.GetRowCount();i++){		
				alert(ds_SMBZ120C_CHECK.GetColumn(i,"COMPS") + " , "
					+ ds_SMBZ120C_CHECK.GetColumn(i,"PYVND") + " = "
					+ ds_SMBZ120C_CHECK.GetColumn(i,"RTMSG"));	
			}
			//return;	
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setSMBZ120C"){
		//마감내역FLAG저장
		var sKind		= "setmst040";						
		var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " MAJCD=" + quote("SMB120PFMAGAM");
		sArgument += " MJINT=" + quote(substr(cal_JSOYM.Value,0,6));
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		//마감history에 저장
		var sKind		= "setmst049";						
		var sMethodName = "service::pscomlib/saveMST049.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " PGSRC=" + quote("SMBZ120C");
		sArgument += " PGMNM=" + quote("월미수금마감작업");
		sArgument += " PDATE=" + quote(substr(cal_JSOYM.Value,0,6));
		sArgument += " JOBID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
		sArgument += " BIGO1=" + quote("");
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_SMBZ120C.ClearData();
	ds_SMBZ120C_CHECK.ClearData();
}


