function setExcel()
{
	var fName   = "업체별차종별 월납입현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|업체별차종별 월납입현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

