function getSMDZ121P(){

	if(!validationChk()){
		return;
	}
	var sPYVND1="";
	var sPYVND2="";
	var sJPYYM1="";
	var sJPYYM2="";
	var sPRDCD1="";
	var sPRDCD2="";
	var sCARCD1="";
	var sCARCD2="";	
	//업체
	if(length(ed_PYVND1.Text)==0){
		sPYVND1="00000000";
	}
	else{
		sPYVND1=ed_PYVND1.Text;
	}	
	if(length(ed_PYVND2.Text)==0){
		sPYVND2="99999999";
	}
	else{
		sPYVND2=ed_PYVND2.Text;
	}
	//전표
	if(cmb_JPYYM1.Value == ""){
		sJPYYM1="000000";
	}
	else if(cmb_JPYYM1.Value != ""){
		sJPYYM1=substr(cmb_JPYYM1.Value,0,6);
	}
	if(cmb_JPYYM2.Value == ""){
		sJPYYM2="999999";
	}
	else if(cmb_JPYYM2.Value != ""){
		sJPYYM2=substr(cmb_JPYYM2.Value,0,6);
	}
	//제품
	if(length(ed_PRDCD1.Text)==0){
		sPRDCD1="";
	}
	else{
		sPRDCD1=ed_PRDCD1.Text;
	}	
	if(length(ed_PRDCD2.Text)==0){
		sPRDCD2="999";
	}
	else{
		sPRDCD2=ed_PRDCD2.Text;
	}
	//차종
	if(length(ed_CARCD1.Text)==0){
		sCARCD1="";
	}
	else{
		sCARCD1=ed_CARCD1.Text;
	}	
	if(length(ed_CARCD2.Text)==0){
		sCARCD2="999";
	}
	else{
		sCARCD2=ed_CARCD2.Text;
	}

	getSYSDATE(); //SYSTEM 날짜
	var sysDt =ds_SYSDATE.GetColumn(0,"DT");
	var systm =ds_SYSDATE.GetColumn(0,"TM");	

	var sKind		= "getSMDZ121P";						
	var sMethodName = "service::pssallib/selectSMDZ121P.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SMDZ121P=ds_SMDZ121P";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@PYVND1=" + quote(sPYVND1); //업체1
	sArgument += "@PYVND2=" + quote(sPYVND2); //업체2
	sArgument += "@JPYYM1=" + quote(sJPYYM1); //전표일자1
	sArgument += "@JPYYM2=" + quote(sJPYYM2); //전표일자1
	sArgument += "@PRDCD1=" + quote(sPRDCD1); //제품1
	sArgument += "@PRDCD2=" + quote(sPRDCD2); //제품2
	sArgument += "@CARCD1=" + quote(sCARCD1); //차종1
	sArgument += "@CARCD2=" + quote(sCARCD2); //차종2
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
		return;
	}

	//Ubi0.setJrfFileDir("http://10.74.139.50/ui/PSSALLIB/REPORT/");
	//Ubi0.setJrfFileDir("http://10.72.209.23/ui/PSSALLIB/REPORT/");
	Ubi0.setJrfFileDir(G_baseURL+"ui/PSSALLIB/REPORT/");
	Ubi0.setJrfFileName("SMDZ121P.jrf");
	Ubi0.SetDataset("ds_SMDZ121P", "ds_SMDZ121P");
	Ubi0.SetArgument("PYVND1",sPYVND1);
	Ubi0.SetArgument("PYVND2",sPYVND2);	
	Ubi0.SetArgument("JPYYM1",sJPYYM1);	
	Ubi0.SetArgument("JPYYM2",sJPYYM2);
	Ubi0.SetArgument("SYSDATE",sysDt);
	Ubi0.SetArgument("SYSTIME",systm);	
	Ubi0.Retrieve();

}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var sdVal = substr(cmb_JPYYM1.Value,0,6);
	var edVal = substr(cmb_JPYYM2.Value,0,6);
	cmb_JPYYM1.BKColor="white";
	cmb_JPYYM2.BKColor="white";	

	if(sdVal > edVal)
	{
		cmb_JPYYM1.BKColor="paleturquoise";
		cmb_JPYYM2.BKColor="paleturquoise";
		alert(setPopMessage("3049"));
		result =false;
	}
	return result;
}

