function setExcel()
{
	var fName   = "득의선품목관리마스타현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|득의선품목관리마스타현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

