function on_LoadCompleted(obj){
	getTitle();	
	getComCode();
	//getMst040();
	//cal_PLNYM.Value = nowDate;
	gFcOnTopButtonColor("0"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
