function on_LoadCompleted(obj){
	getTitle();	
	getDesign();	
	//getComCode();
	//ed_syyyymmdd.Value = nowDate;	
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)		
    //fn_Align();
    ed_COMPS_D.Text = COMPS;
    ed_PLDCD_D.Text = PLTCD;
    ed_PYVND_F_D.Text = PYVND1;
    ed_PYVND_T_D.Text = PYVND2;
    ed_prdcd_from_D.Text = PRDCD1;
    ed_prdcd_to_D.Text = PRDCD2;
    ed_AD_D.Text = MGMCD;
    ed_PLNYM_D.Text = PLNYM; //left(PLNYM,2)+"/"+substr(PLNYM,2,2);
    var rdogub_d = GUB;
    if (GUB == "1" || GUB == "3"){
		ed_PRDCD_D.Text = CODE;
		ed_PYVND_D.Text = CODE1;
	}else{
		ed_PRDCD_D.Text = CODE1;
		ed_PYVND_D.Text = CODE;
	}
	getSPMC128QD();
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSPMC128QD(){
	var sKind		= "getSPMC128QD";						
	var sMethodName = "service::pssallib/selectSPMC128QD.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SPMC128QD=ds_SPMC128QD";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ed_COMPS_D.Value);//사업부
	sArgument += " PLTCD=" + quote(ed_PLDCD_D.Value);//공장구분
    sArgument += " PLNYM=" + quote(ed_PLNYM_D.Value); //계획년월 
	sArgument += " PYVND1=" + quote(ed_PYVND_F_D.Value);
	sArgument += " PYVND2=" + quote(ed_PYVND_T_D.Value);	
	sArgument += " PRDCD1=" + quote(ed_prdcd_from_D.Value);
	sArgument += " PRDCD2=" + quote(ed_prdcd_to_D.Value);
    sArgument += " MGMCD=" + quote(ed_AD_D.Value); //denso,asmo구분
    sArgument += " SELPRDCD=" + quote(ed_PRDCD_D.Value); //선택제품
    sArgument += " SELPYVND=" + quote(ed_PYVND_D.Value); //

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "월판매계획대실적집계표상세";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월판매계획대실적집계표상세";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else if(svcId=="getSPMC128QD"){
		// 조회건수가 없을경우
		if(ds_SPMC128QD.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	// Ctrl + V
	/*} else if(bCtrl == 1 && nChar == 86) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardPaste(obj, strSeparator);

	// Ctrl + Z
	} else if(bCtrl == 1 && nChar == 90) {

		// grd_Standard.AreaSelect = true;
		grd_fn_CellReset(obj);*/
	}
}

