/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();	
	getComCode();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 		
	ca_plnym.Value = nowDate;	
	MainGrid.UserData = MainGrid.Height;
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	//ds_VComCode_PLNT.InsertRow(0);
	//ds_VComCode_PLNT.SetColumn(0,"CODE2","");
	//ds_VComCode_PLNT.SetColumn(0,"CODE_NAME","[전체]");
	cb_searchCodeName1.index = 0;
}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSPME128Q()
{
	if(!validationChk()){
		return;
	}

	switch (rdoggub.Value){
		case '1' : 
			MainGrid.SetCellProp("Head", 0, "text", "거래처");
			MainGrid.SetCellProp("Head", 1, "text", "거래처명");
			MainGrid.SetColProp(2,"Width",0);
			MainGrid.SetColProp(3,"Width",0);
			break;
		case '2' :
			MainGrid.SetCellProp("Head", 0, "text", "차종");
			MainGrid.SetCellProp("Head", 1, "text", "차종명");
			MainGrid.SetColProp(2,"Width",0);
			MainGrid.SetColProp(3,"Width",0);
			break;
		case '3' :
			MainGrid.SetCellProp("Head", 0, "text", "규격");
			MainGrid.SetCellProp("Head", 1, "text", "규격명");
			MainGrid.SetColProp(2,"Width",0);
			MainGrid.SetColProp(3,"Width",0);
			break;
		case '4' :
			MainGrid.SetCellProp("Head", 0, "text", "거래처");
			MainGrid.SetCellProp("Head", 1, "text", "거래처명");
			MainGrid.SetCellProp("Head", 2, "text", "차종");
			MainGrid.SetCellProp("Head", 3, "text", "차종명");
			MainGrid.SetColProp(2,"Width",60);
			MainGrid.SetColProp(3,"Width",120);
			break;
		case '5' :
			MainGrid.SetCellProp("Head", 0, "text", "차종");
			MainGrid.SetCellProp("Head", 1, "text", "차종명");
			MainGrid.SetCellProp("Head", 2, "text", "규격");
			MainGrid.SetCellProp("Head", 3, "text", "규격명");
			MainGrid.SetColProp(2,"Width",60);
			MainGrid.SetColProp(3,"Width",120);
			break;
		case '6' :
			MainGrid.SetCellProp("Head", 0, "text", "규격");
			MainGrid.SetCellProp("Head", 1, "text", "규격명");
			MainGrid.SetCellProp("Head", 2, "text", "거래처");
			MainGrid.SetCellProp("Head", 3, "text", "거래처명");
			MainGrid.SetColProp(2,"Width",60);
			MainGrid.SetColProp(3,"Width",120);
			break;
		case '7' : 
			MainGrid.SetCellProp("Head", 0, "text", "제품");
			MainGrid.SetCellProp("Head", 1, "text", "제품명");
			MainGrid.SetColProp(2,"Width",0);
			MainGrid.SetColProp(3,"Width",0);
			break;	

	}
	//2019.03.14 제품코드 구분 코드 추가

	var sqlType="D";	//기본
	if(rdoggub.Value=='7'){
		sqlType="P";	//제품코드용
	}
	//end
	var p_pyvnd1,p_pyvnd2,p_prdcd1,p_prdcd2;
	p_pyvnd1 = iif (length(trim(ed_PYVND_F.Text)) == 0, '0000', trim(ed_PYVND_F.Text));
	p_pyvnd2 = iif (length(trim(ed_PYVND_T.Text)) == 0 , '9999', trim(ed_PYVND_T.Text));
	p_prdcd1 = iif (length(trim(ed_prdcd_from.Text)) == 0 , '  ', trim(ed_prdcd_from.Text));
	p_prdcd2 = iif (length(trim(ed_prdcd_to.Text)) == 0 , 'ZZ', trim(ed_prdcd_to.Text));

	var rolcdgb;
	if (cb_ad.Value == "D"){
		rolcdgb = "('2','4')";
	}else if (cb_ad.Value == "O"){
		rolcdgb = "('6','8')";
	}else{
		rolcdgb = "";
	}
	var sKind		= "getSPME128Q";						
	var sMethodName = "service::pssallib/selectSPME128Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SPME128Q=ds_SPME128Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);//사업부
    sArgument += "@PLNYM=" + quote(substr(ca_plnym.Value,0,6)); //계획년월 
	sArgument += "@PYVND1=" + quote(p_pyvnd1);
	sArgument += "@PYVND2=" + quote(p_pyvnd2);	
	sArgument += "@PRDCD1=" + quote(p_prdcd1);
	sArgument += "@PRDCD2=" + quote(p_prdcd2);
	sArgument += "@ROLCD=" + quote(rolcdgb); //생산구분
    sArgument += "@G_GUB="  + quote(rdoggub.Value);	//구분
    sArgument += "@SQL_GUB="  + quote(sqlType);	//SQL구분(2019.03.14추가)
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	ca_plnym.BKColor="white";

	if (ca_plnym.Value == "" )
	{
		ca_plnym.BKColor="paleturquoise";
		alert(setPopMessage("4715"));//[알림]기준일자를 입력하세요.
		setMessage("4715");	
		result = false;
	}
	return result;
}

function rdoggub_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_SPME128Q.ClearData();
	getSPME128Q();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = substr(ca_plnym.Value,0,6)+"월판매계획대실적집계표";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월판매계획대실적집계표";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
