}
/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
//	MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");		
}

function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	cb_searchCodeName1.index = 0;
}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSPMZ030(){
	//조회
	//clearEditObject();  //초기화
	var sKind		= "getSPMZ030";						
	var sMethodName = "service::pssallib/selectSPMZ030.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SPMZ030=ds_SPMZ030";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1013"));  //[알림] 신규입력입니다.
		setMessage("1013");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setSPMZ030(){
 	http.Sync = true;
	var sKind		= "SAVE_SPMZ030";						
	var sMethodName = "service::pssallib/insertSPMZ030.do";
	var sInDataSet  = "ds_SPMZ030=ds_SPMZ030:U";
	var sOutDataSet = "ds_SPMZ030_CHECK=ds_SPMZ030_CHECK";          // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	//sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	//sArgument += " PYVND=" + quote(ed_pyvnd.Text);
	//sArgument += " COMYM=" + quote(substr(cal_COMYM.Value,0,6));

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
 }

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "월수주갱신업체마스타 ";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월수주갱신업체마스타";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_SPMZ030_CHECK.GetRowCount() > 0){			
		for(var i=0;i<ds_SPMZ030_CHECK.GetRowCount();i++){		
			alert(ds_SPMZ030_CHECK.GetColumn(i,"PYVND") + "=" + ds_SPMZ030_CHECK.GetColumn(i,"RTMSG"));	
		}		
		getSPMZ030();        //error항목이 존재시에는 재조회		
		return;		
	}			

	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

function ds_SPMZ030_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if((strColumnID == "MU0CD") || (strColumnID == "MU1CD") || (strColumnID == "MU2CD") || (strColumnID == "MU3CD") ||
	   (strColumnID == "MU4CD") || (strColumnID == "MU5CD") || (strColumnID == "MU6CD")){
		if (!((varNewValue == '') || (varNewValue == ' ') || (varNewValue == '1') || (varNewValue == '2'))){
			alert("'공백','1','2'만 가능합니다.");
			return false;
		}
	}
	if (strColumnID == "PYVND"){
		http.Sync 	= true;
		var sKind		= "getOGMPrtno";		
		var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " CUSNO=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync 	= false;
		if (ds_CUSMAS1.GetRowCount() < 1){
			alert(setPopMessage("1002"));
			setMessage("1002");	
			return false;
		}
		pyvnd_NM=ds_CUSMAS1.GetColumn(0,"CUSNM");
	}
	if (strColumnID == "SHPTO"){
		if (!((varNewValue == '0') || (varNewValue == '12'))){
			alert("'0','12'만 가능합니다.");
			return false;
		}
	}
	return true;
}

function ds_SPMZ030_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "PYVND"){
		ds_SPMZ030.SetColumn(nRow, "CUSNM", pyvnd_NM); // 거래처명 설정
		ds_SPMZ030.SetColumn(ds_SPMZ030.Row, "SHPTO", '0'); // default값 설정
	}
	http.usewaitcursor = true;
}
