}

function fn_Search()
{
	getSPMZ030();
}

function fn_Input()
{
	if(checkInsertCount("ds_SPMZ030")== false) return;
	if(ds_SPMZ030.rowcount < 1) fn_Search();
	ds_SPMZ030.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
	ds_SPMZ030.AddRow();
	ds_SPMZ030.SetColumn(ds_SPMZ030.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
	ds_SPMZ030.FireEvent=true;
}

function fn_Delete()
{
	if(ds_SPMZ030.rowcount < 1) return;
	ds_SPMZ030.DeleteRow(ds_SPMZ030.currow);
	alert(setPopMessage("1011"));     //[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.
	setMessage("1011");	
}

function fn_Save()
{
	setSPMZ030();
}

