function setSPMZ109C(){
	if(length(trim(Div0.cal_PLNYM.Value)) < 8){
		alert(setPopMessage("4020"));  //[[확인] 마감년월을 확인바랍니다.
		setMessage("4020");	
		Div0.cal_PLNYM.SetFocus();
		return;
	}

	if(confirm(substr(Div0.cal_PLNYM.Value,0,4)+"년 "+substr(Div0.cal_PLNYM.Value,4,2)+"월 마감작업입니다. 작업하시겠습니까? ") == false) return;
	getMst040();
	if(substr(next_month,0,6) != substr(Div0.cal_PLNYM.Value,0,6)) {
		alert(setPopMessage("4020")); //  [확인] 마감년월을 확인바랍니다.
		setMessage("4020");	
		return;
	}
	/* 삭제 : 2019.11.28 NEWDIONE로 SSO관리전환
	//as400 USERID Get
	http.Sync = true;
