	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));  // [경고] Display Session User ID가 존재하지 않습니다. 정보관리팀에 문의바랍니다.
		setMessage("1014");
		return;
	}
	callUser = ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	callPass = ds_ssoaccount.GetColumn(0,"SYSTEM_PW");
	*/
	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}

	var groupCOMPS;
	var mst040COMPS;
	if (Div0.cb_searchCodeName1.Value == "S"){
		groupCOMPS = "PS";
		mst040COMPS = "C1";
	}else if(Div0.cb_searchCodeName1.Value == "E"){
		groupCOMPS = "PE";
		mst040COMPS = "E1";
	}else{
		groupCOMPS = "X ";
		mst040COMPS = "XX";
	}

	/* stored procedure실행
	http.Sync 		= true;
	var sKind		= "getSPMZ109C";						
	var sMethodName = "service::pssallib/procSPMZ109C.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";											// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " PLNYM=" + quote(substr(cal_PLNYM.Value,0,6));
	sArgument += " ERRCD=" + quote("0");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync 		= false;
	*/

	/*		//리모트콜 실행 (spmz109rc1용)
	param = groupCOMPS + substr(cal_PLNYM.Value,0,6);
	//for(i=length(param)+1;i<=90;i++){
	for(i=length(param)+1;i<=30;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';
	*/

	//리모트콜 실행(spmz109rc2용)
	param = groupCOMPS + substr(Div0.cal_PLNYM.Value,0,6);
	for(i=length(param)+1;i<=30;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	st_Msg.Visible = true;
	Div0.Enable = false;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall("*LIBL","SMDZ127CP","'1111' '20120801' '0'",1200);
		//Var rtn2 = WTA_RemoteCall0.RC_RmtCmd("CALL PGM(SPMZ109SP) PARM('C1' '201208' '0')");
		//var rtn2 = WTA_RemoteCall0.RC_RmtCmd("CALL PGM(SMDZ127CP) PARM('1111' '20120801' '0')");  //성공했음
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),40,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				//마감history에 저장
				var sKind		= "setmst049";						
				var sMethodName = "service::pscomlib/saveMST049.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(mst040COMPS);
				sArgument += " PGSRC=" + quote("SPMZ109C");
				sArgument += " PGMNM=" + quote("월판매계획마감작업");
				sArgument += " PDATE=" + quote(SUBSTR(Div0.cal_PLNYM.Value,0,6));
				sArgument += " JOBID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
				sArgument += " BIGO1=" + quote("");
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				/* RMXXX
				var sKind		= "setmst040";						
				var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(mst040COMPS);
				sArgument += " MAJCD=" + quote("SPM110PFMAGAM");
				sArgument += " MJINT=" + quote(substr(Div0.cal_PLNYM.Value,0,6));
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
				*/
				st_Msg.Visible = false;
				//Div0.Enable = true;
				alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
				setMessage("1001");	
				return;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),40,3) == "888") {
				//alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				st_Msg.Visible = false;
				Div0.Enable = true;
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				return false;
			} else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),40,3) == "777") {
				//alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				st_Msg.Visible = false;
				Div0.Enable = true;
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),40,3) == "666") {
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("4020")); //[확인] 마감년월을 확인바랍니다.
				setMessage("4020");	
				return false;
			}else {
				//alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				st_Msg.Visible = false;
				Div0.Enable = true;
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(rtnCode);
		if (rtnCode == 0){
			alert(setPopMessage("1001"));
			setMessage("1001");
		}else if (rtnCode == 1){
			alert("타사용자가 사용중입니다. 확인바랍니다.");
			//setMessage("1002");
		}else{
			alert(setPopMessage("1002"));
			setMessage("1002");
		}
	}
}

