var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1");
var Lib2 = G_ds_user.GetColumn(0,"LIB2");
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var nowDate = today();


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();	
	ed_comps.Value = P_COMPS;
	ed_pyvnd.Value = P_PYVND;
	if (P_PLTCD=="ZZ")
	{
		ed_pltcd.Value = "[전체]";
		P_PLTCD="";
	}else{
		ed_pltcd.Value = P_PLTCD;
	}
	ed_plnym.Value = substr(P_YYMM,0,4)+'년 '+substr(P_YYMM,4,2)+'월';
	ed_AD.Value = cb_ad.Text;
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)		
	getSPYC900QD();
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
}

function getSPYC900QD()
{
	if (length(ed_pyvnd.Value) == 4){
		var sKind		= "getCGMPARMDA";						
		var sMethodName = "service::pscomlib/selectCGMPARMDA.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_CGMPARMDA=ds_CGMPARMDA";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";

		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " CUSNO=" + quote(ed_pyvnd.Value); //득의선

		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	}else{	
		ed_vname.Value="";
	}
	if (P_PLNGB == '1'){
		MainGrid.SetCellProp("Head", 3, "text", "기본계획수량");
		MainGrid.SetCellProp("Head", 4, "text", "기본계획금액");
	}else{
		MainGrid.SetCellProp("Head", 3, "text", "수정계획수량");
		MainGrid.SetCellProp("Head", 4, "text", "수정계획금액");
	}
	var sKind		= "getSPYC900QD";						
	var sMethodName = "service::pssallib/selectSPYC900QD.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SPYC900QD=ds_SPYC900QD";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(P_COMPS); //사업부
	sArgument += "@PYVND=" + quote(P_PYVND); //득의선
	sArgument += "@PLTCD=" + quote(P_PLTCD); //plant CD
    sArgument += "@PLNYM=" + quote(P_YYMM); //계획년월 
    sArgument += "@MGMCD=" + quote(cb_ad.Value); //denso,asmo구분
    sArgument += "@PLNGB=" + quote(P_PLNGB); //계획구분
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-12-06
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = P_YYMM+"월판매계획대실적상세";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월판매계획대실적상세";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else if(svcId=="getSPYC900Q"){
		// 조회건수가 없을경우
		if(ds_SPYC900QD.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	// Ctrl + V
	/*} else if(bCtrl == 1 && nChar == 86) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardPaste(obj, strSeparator);

	// Ctrl + Z
	} else if(bCtrl == 1 && nChar == 90) {

		// grd_Standard.AreaSelect = true;
		grd_fn_CellReset(obj);*/
	}
}

