function on_LoadCompleted(obj){
	getTitle();	
	getDesign();	
	if (P_COMPS=="ZZ")
	{
		ed_comps.Value = "[전체]";
		P_COMPS="";
	}else{
		ed_comps.Value = P_COMPS;;
	}

	if (P_PLTCD=="ZZ")
	{
		ed_pltcd.Value = "[전체]";
		P_PLTCD="";
	}else{
		ed_pltcd.Value = P_PLTCD;
	}
	ed_plnymF.Value = substr(P_YYMMF,0,4)+'년 '+substr(P_YYMMF,4,2)+'월';
	ed_plnymT.Value = substr(P_YYMMT,0,4)+'년 '+substr(P_YYMMT,4,2)+'월';
	ed_plngb.Value = cb_plngb.Text;
	ed_itcls1.Value =cb_itcls1.Text; //P_PRDCD+" "+P_PRDNM;
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)		
	getSPYC906QD();
}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
}

function getSPYC906QD()
{
	var month1 = substr(AddMonth(ca_plnymF.Value,1),0,6);
	var month2 = substr(AddMonth(ca_plnymF.Value,2),0,6);
	var month3 = substr(AddMonth(ca_plnymF.Value,3),0,6);
	var month4 = substr(AddMonth(ca_plnymF.Value,4),0,6);
	var month5 = substr(AddMonth(ca_plnymF.Value,5),0,6);
	var month6 = substr(AddMonth(ca_plnymF.Value,6),0,6);
	var month7 = substr(AddMonth(ca_plnymF.Value,7),0,6);
	var month8 = substr(AddMonth(ca_plnymF.Value,8),0,6);
	var month9 = substr(AddMonth(ca_plnymF.Value,9),0,6);
	var month10 = substr(AddMonth(ca_plnymF.Value,10),0,6);
	MainGrid.SetCellProp("Head", 3, "text", substr(ca_plnymF.Value,0,4)+"/"+substr(ca_plnymF.Value,4,2));
	MainGrid.SetCellProp("Head", 4, "text", substr(month1,0,4)+"/"+substr(month1,4,2));
	MainGrid.SetCellProp("Head", 5, "text", substr(month2,0,4)+"/"+substr(month2,4,2));
	MainGrid.SetCellProp("Head", 6, "text", substr(month3,0,4)+"/"+substr(month3,4,2));
	MainGrid.SetCellProp("Head", 7, "text", substr(month4,0,4)+"/"+substr(month4,4,2));
	MainGrid.SetCellProp("Head", 8, "text", substr(month5,0,4)+"/"+substr(month5,4,2));
	MainGrid.SetCellProp("Head", 9, "text", substr(month6,0,4)+"/"+substr(month6,4,2));
	MainGrid.SetCellProp("Head", 10, "text", substr(month7,0,4)+"/"+substr(month7,4,2));
	MainGrid.SetCellProp("Head", 11, "text", substr(month8,0,4)+"/"+substr(month8,4,2));
	MainGrid.SetCellProp("Head", 12, "text", substr(month9,0,4)+"/"+substr(month9,4,2));
	MainGrid.SetCellProp("Head", 13, "text", substr(month10,0,4)+"/"+substr(month10,4,2));
	MainGrid.SetCellProp("Head", 14, "text", substr(ca_plnymT.Value,0,4)+"/"+substr(ca_plnymT.Value,4,2));

	var sKind		= "getSPYC906QD";						
	var sMethodName = "service::pssallib/selectSPYC906QD.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SPYC906QD=ds_SPYC906QD";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(P_COMPS); //사업부
	sArgument += "@PLTCD=" + quote(P_PLTCD); //공장구분
    sArgument += "@PLNYM_F=" + quote(P_YYMMF); //시작계획년월 
    sArgument += "@PLNYM_T=" + quote(P_YYMMT); //종료계획년월 
    sArgument += "@PLNYM_1=" + quote(month1); //계획년월+1 
    sArgument += "@PLNYM_2=" + quote(month2); //계획년월+2
    sArgument += "@PLNYM_3=" + quote(month3); //계획년월+3
    sArgument += "@PLNYM_4=" + quote(month4); //계획년월+4
    sArgument += "@PLNYM_5=" + quote(month5); //계획년월+5 
    sArgument += "@PLNYM_6=" + quote(month6); //계획년월+6 
    sArgument += "@PLNYM_7=" + quote(month7); //계획년월+7
    sArgument += "@PLNYM_8=" + quote(month8); //계획년월+8 
    sArgument += "@PLNYM_9=" + quote(month9); //계획년월+9
    sArgument += "@PLNYM_10=" + quote(month10); //계획년월+10
    sArgument += "@PLNGB=" + quote(P_PLNGB); //계획구분
    sArgument += "@PRDCD=" + quote(P_PRDCD); //제품코드

	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


