function setExcel()
{
	var fName   = "년제품별판매계획대실적상세";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|년제품별판매계획대실적상세";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else if(svcId=="getSPYC906QD"){
		// 조회건수가 없을경우
		if(ds_SPYC906QD.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
