function on_LoadCompleted(obj)
{
	getTitle();
	MainGrid.Visible = false;
	gFcOnTopButtonColor("2.3"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                            //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                            //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                            //                       2.7:조회,추가,저장,excel 	
	ed_yyyy0.SetFocus();
	if(comps=="E1"){
		MainGrid.tab1.Grid0.Visible = false;
		MainGrid.tab1.Grid1.Visible = true;
	}else{
		MainGrid.tab1.Grid0.Visible = true;
		MainGrid.tab1.Grid1.Visible = false;
	}
}

function onCellCilck(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ed_yyyy.Value < 2010 or ed_yyyy.Value >2999){
		alert("시작년도 확인바랍니다.");
		return; 
	}
	var tableNm  = ds_list.GetColumn(nRow,"tableNm");
	var yyyymm   = ds_list.GetColumn(nRow,"date");
	var colCnt   = ds_list.GetColumn(nRow,"columnCnt");
	var pgmName  = ds_list.GetColumn(nRow,"id");	
	var fileType  = ds_list.GetColumn(nRow,"fileType");		

	if(nCell == 3)	{
		if (ds_CVTSPY200C0.GetColumn(0,"STEP4") == '1'){
			alert("불가능합니다. 이미 확정작업이 완료되었습니다. ");
			return;
		}
		if(fileType == "1"){
			uploadExcel();
			if(!(checkExcel())){
				if(comps=="E1"){
					for (i=0;i<ds_main_PE.GetRowCount();i++){
						var pspno_p = ds_main_PE.GetColumn(i,"DNPN");
						if(length(pspno_p) > 15){
							ds_main_PE.SetColumn(i,"DNPN",substr(trim(pspno_p),1,14));
						}
					}
					saveDataSet_PE(tableNm,pgmName);
				}else{
					for (i=0;i<ds_main.GetRowCount();i++){
						var pspno_p = ds_main.GetColumn(i,"DNPN");
						if(length(pspno_p) > 15){
							ds_main.SetColumn(i,"DNPN",substr(trim(pspno_p),1,14));
						}
					}
					saveDataSet(tableNm,pgmName);
				}
			flagCheck();
			}
		} else if (fileType == "2") {
			uploadText();
			saveDataSet(tableNm,pgmName);		
		} else {
			uploadCsv(colCnt);
			saveDataSet(tableNm,pgmName);		
		}
	}
}

function Tab_tab5_grd_board_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 2){
		if (ds_CVTSPY200C0.GetColumn(0,"STEP4") == '1'){
			alert("불가능합니다. 이미 확정작업이 완료되었습니다. ");
			return;
		}

		if (ds_CVTSPY200C0.GetColumn(0,"STEP3") == '1'){
			if(confirm("이미 평균단가산출 작업이 진행되어 있습니다. 재작업하시겠습니까? ") == false) return;
		}
		//평균단가산출
		var startYY = ed_yyyy.Value;
		var next1YY = parseInt(ed_yyyy.Value)+1;
		var next2YY = parseInt(ed_yyyy.Value)+2;
		var next3YY = parseInt(ed_yyyy.Value)+3;
		var next4YY = parseInt(ed_yyyy.Value)+4;
		var sKind		= "SAVE05";						
		var sMethodName = "service::pscomlib/insertSPYDSKRPF.do";
		var sInDataSet  = "";
		var sOutDataSet = "";
		var arg   = "";
		arg += " LIB2=" + quote(Lib2);
		arg += " PYYYY=" + quote(ed_yyyy.Value);
		arg += " PYYY1=" + quote(next1YY);
		arg += " PYYY2=" + quote(next2YY);
		arg += " PYYY3=" + quote(next3YY);
		arg += " PYYY4=" + quote(next4YY);
		arg += " PLNGB=" + quote(cb_plngb.Value);
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, arg, "callback");
		http.Sync = false;
		flagCheck();
	}
}

function uploadExcel()
{
	if(comps=="E1"){
		ds_main_PE.DeleteAll();
	}else{
		ds_main.DeleteAll();
	}
	FileDialog0.Filter = "ALL File(xls.*)|*.xls";	
	FileDialog0.Type = "OPEN";
	if (!FileDialog0.Open()) {
		alert(setPopMessage("1009"));	// 20110103l-koowgnojeel, 1009 == 파일 선택 창을 여는데 실패하였습니다.
		return;
	}
	var file_name = FileDialog0.FileName;
	var file_path = FileDialog0.FilePath;
	var path = file_path + "\\" + file_name;
	setMessageDirect(path);
	if(comps=="E1"){
		ext_ExcelImportByIndex(path,0,"ds_main_PE",1,1,0,2);  
	}else{
		ext_ExcelImportByIndex(path,0,"ds_main",1,1,0,2);  
	}
	//ext_ExcelImportByIndex(strExcelFilename, intSheetIndex, strTargetDatasetID, [intUseColInfo, intExp, intDecimal,intStartRow]); 
	//ext_ExcelImportByIndex(path,0,"Dataset0",0);
	//MainGrid.tab1.Grid1.CreateFormat(); 

}
function checkExcel()
{
	var kakeDataset;
	if(comps=="E1"){
		kakeDataset = "ds_main_PE";
	}else{
		kakeDataset = "ds_main";
	}
	if (object(kakeDataset).GetRowCount() > 0){
		var chkExcelData = true;
		var chkMassage="";
		for(var z=0;z<object(kakeDataset).GetRowCount();z++){
			if (length(trim(object(kakeDataset).GetColumn(z,"DNPN"))) < 1 || length(object(kakeDataset).GetColumn(z,"DNPN")) > 15){
				chkExcelData = false;
				chkMassage +=  "행 "+(z+1)+"번 " + "DENSO품번 오류"+"\n";
			}
			if (length(trim(object(kakeDataset).GetColumn(z,"CustomerNumber"))) != 4) {
				chkExcelData = false;
				chkMassage +=  "행 "+(z+1)+"번 " + "거래처코드 오류(공백,자리수 Check)"+"\n";
			}else{
				if (!numberChk(object(kakeDataset).GetColumn(z,"CustomerNumber"))){
					chkExcelData = false;
					chkMassage +=  "행 "+(z+1)+"번 " + "거래처코드 오류(숫자 Check)"+"\n";
				}
			}
		}
		if (!chkExcelData){
			alert("Excel Data가 형식에 맞지않습니다."+"\n"+chkMassage);
			object(kakeDataset).DeleteAll();
			Div2.Visible = false;
			return false;
		}else{
			return true;
		}
	}
}
