function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		//alert(setPopMessage("1002"));
		//setMessage("1002");
		alert("문제가 발생하였습니다. 정보관리팀으로 연락바랍니다.");
	}else if(svcId=="SAVE00"){
		// 조회건수가 없을경우
		if(comps=="E1"){
			if(ds_main_PE.rowcount <= 0){
				alert(setPopMessage("1003"));
				setMessage("1003");	
			// 정상 
			} else {
				//alert(setPopMessage("1004"));
				//setMessage("1004");				
				alert("정상적으로 처리되었습니다.");
			}
		}else{
			if(ds_main.rowcount <= 0){
				alert(setPopMessage("1003"));
				setMessage("1003");	
			// 정상 
			} else {
				//alert(setPopMessage("1004"));
				//setMessage("1004");				
				alert("정상적으로 처리되었습니다.");
			}
		}
	}else if(svcId=="getCVTSPY200C1"){
		// 조회건수가 없을경우
		if(ds_CVTSPY200C1.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
	}else if(svcId=="SAVE01"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}else if(svcId=="SAVE05"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}

function Button0_OnClick(obj)
{
	if (ed_yyyy0.Value < 2012 or ed_yyyy0.Value >2999){
			alert("시작년도 확인바랍니다.");
			return;
	}
	MainGrid.Visible=true;
	ed_yyyy0.Visible = false;
	ed_y.Visible =false;
	cb_plngb0.Visible =false;
	ed_select.Visible =false;
	Button0.Visible = false;
	ed_yyyy.Text = ed_yyyy0.Text;
	cb_plngb.Text = cb_plngb0.Text;
	flagCheck();

	MainGrid.tab6.Edit0.Text = company;
	MainGrid.tab6.Edit1.Text = ed_yyyy.Value;

}

function flagCheck()
{
	var sKind		= "getCVTSPY200C0";						
	var sMethodName = "service::pscomlib/selectCVTSPY200C0.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_CVTSPY200C0=ds_CVTSPY200C0";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@PYYYY=" + quote(ed_yyyy.Value);
	sArgument += "@PLNGB=" + quote(cb_plngb.Value);
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
}

function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("2.3"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "Maker관리";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|Maker관리";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/*--------------------------------------------------------------
 * 아래 함수는 콩통함수 이므로 모든 화면에 필요
 * 함수명 변경 불가
 * 버튼 컨트롤
----------------------------------------------------------------*/
function fn_Excel()
{
	var fName  ;
	var arrSheets ;  
	switch (MainGrid.TabIndex){
	case 0 : 
		fName   = "HostUP";
		arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab1.Grid0|HostUP";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 1 : 
		fName   = "품번오류"; 
		arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab2.Grid0|품번오류";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 2 : 
		fName   = "거래처오류";
		arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab3.Grid0|거래처오류";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 3 : 
		fName   = "동일품번단가상이";
		arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab4.Grid0|동일품번단가상이";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 4 :
		alert("지원하지 않는 기능입니다.");
	case 5 : 
		if (ds_CVTSPY200C0.GetColumn(0,"STEP3") != '1'){
			alert("평균단가산출작업후 Excel Down가능합니다.");
			return;
		}
		fName   = "년판매계획CHECK";
		arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab6.Grid0|년판매계획CHECK";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	case 6 :
		alert("지원하지 않는 기능입니다.");
	case 7 : 
		fName   = "5개년판매계획";
		arrSheets  =  Array();  
		arrSheets[0]  = "MainGrid.tab8.Grid0|5개년판매계획";
		createExcelSheetS2(FileDialog0, fName ,arrSheets);
		return;
	}
}

