function MainGrid_tab7_Button0_OnClick(obj)
{
	if (ds_CVTSPY200C0.GetColumn(0,"STEP4") == '1'){
		alert("불가능합니다. 이미 확정작업이 완료되었습니다. ");
		return;
	}
	if (ds_CVTSPY200C0.GetColumn(0,"STEP3") != '1'){
		alert("불가능합니다. 평균단가산출작업이 미완료되었습니다. ");
		return;
	}

	if(confirm("확정작업후 data수정은 불가능합니다. 작업하시겠습니까? ") == false) return;


	var startym = ed_yyyy.Value+'04';
	var t_endym = parseInt(ed_yyyy.Value)+5;
	var endym = t_endym+'03';
	//
	http.Sync 		= true;
	var sKind		= "deleteSPY200PF";						
	var sMethodName = "service::pscomlib/deleteSPY200PF.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " STRYM=" + quote(startym);   //년도
	sArgument += " ENDYM=" + quote(endym);   //년도
	sArgument += " PLNGB=" + quote(cb_plngb.Value);      //계획구분
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback1");
	http.Sync 		= false;
}

