#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";

var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
	MainGrid.UserData = MainGrid.Height;
	getMST090();
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	//searchBar.Width = G_ds_design.GetColumn(0,"Width");
	//searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getMST090(){
	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST090PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST090PF=ds_MST090PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " CHKDT=" + quote("SDATE"); //시스템일자로 자동으로 가져옴.
		sArgument += " CHKCNT=" + quote(9); //전표일자
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
//trace(ds_MST090PF.SaveXML());
	MainGrid.SetCellProp("Head", 12, "text", substr(ds_MST090PF.GetColumn(0,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(0,"PRCDT"),6,2));
	MainGrid.SetCellProp("Head", 13, "text", substr(ds_MST090PF.GetColumn(1,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(1,"PRCDT"),6,2));
	MainGrid.SetCellProp("Head", 14, "text", substr(ds_MST090PF.GetColumn(2,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(2,"PRCDT"),6,2));
	MainGrid.SetCellProp("Head", 15, "text", substr(ds_MST090PF.GetColumn(3,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(3,"PRCDT"),6,2));
	MainGrid.SetCellProp("Head", 16, "text", substr(ds_MST090PF.GetColumn(4,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(4,"PRCDT"),6,2));
	MainGrid.SetCellProp("Head", 17, "text", substr(ds_MST090PF.GetColumn(5,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(5,"PRCDT"),6,2));
	MainGrid.SetCellProp("Head", 18, "text", substr(ds_MST090PF.GetColumn(6,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(6,"PRCDT"),6,2));
	MainGrid.SetCellProp("Head", 19, "text", substr(ds_MST090PF.GetColumn(7,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(7,"PRCDT"),6,2));
	MainGrid.SetCellProp("Head", 20, "text", substr(ds_MST090PF.GetColumn(8,"PRCDT"),4,2)+"/"+substr(ds_MST090PF.GetColumn(8,"PRCDT"),6,2));
	st_STDDT.Text=substr(ds_MST090PF.GetColumn(0,"PRCDT"),0,4)+"-"+substr(ds_MST090PF.GetColumn(0,"PRCDT"),4,2)+"-"+substr(ds_MST090PF.GetColumn(0,"PRCDT"),6,2);
}
function getSVDE350Q(){

	http.Sync 	= true;
	var sKind		= "getSC008PR";						
	var sMethodName = "service::pscomlib/selectSC008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SC008PR=ds_SC008PR";
	var sArgument   = "";
		sArgument   += " DTANO='HAC'";
		sArgument   += " LIB1=" + quote(Lib1);	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	var arrHouseT = trim(ds_SC008PR.GetColumn(0,"CTLDT"));
	var arrHouse = "('";
	for (var i=0 ; toNumber(length(arrHouseT)) > i ; i++){
		if (i == length(arrHouseT)-1){
			arrHouse = arrHouse+substr(arrHouseT,i,1) + "')";
		}else{
			arrHouse = arrHouse+substr(arrHouseT,i,1) + "','";
		}
	}

	var sKind		= "getSVDE350Q";						
	var sMethodName = "service::pssallib/selectSVDE350Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVDE350Q=ds_SVDE350Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@HOUSE=" + quote(arrHouse); //창고코드
	sArgument += "@SDAY01=" + quote(ds_MST090PF.GetColumn(0,"PRCDT")); //MST090 날짜1
	sArgument += "@SDAY02=" + quote(ds_MST090PF.GetColumn(1,"PRCDT")); //MST090 날짜2
	sArgument += "@SDAY03=" + quote(ds_MST090PF.GetColumn(2,"PRCDT")); //MST090 날짜3
	sArgument += "@SDAY04=" + quote(ds_MST090PF.GetColumn(3,"PRCDT")); //MST090 날짜4
	sArgument += "@SDAY05=" + quote(ds_MST090PF.GetColumn(4,"PRCDT")); //MST090 날짜5
	sArgument += "@SDAY06=" + quote(ds_MST090PF.GetColumn(5,"PRCDT")); //MST090 날짜6
	sArgument += "@SDAY07=" + quote(ds_MST090PF.GetColumn(6,"PRCDT")); //MST090 날짜7
	sArgument += "@SDAY08=" + quote(ds_MST090PF.GetColumn(7,"PRCDT")); //MST090 날짜8
	sArgument += "@SDAY09=" + quote(ds_MST090PF.GetColumn(8,"PRCDT")); //MST090 날짜9

	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var pyvndVal = ed_PYVND.Text;
	ed_PYVND.BKColor="white";

	if (pyvndVal == "" )
	{
		ed_PYVND.BKColor="paleturquoise";
		alert(setPopMessage("2516"));
		setMessage("2516");	
		result = false;
	}
	return result;
}

