function btn_true_OnClick(obj)
{
	var fName   = "모비스발주현황HOSTUP_정상";
	var arrSheets  =  Array();  

	arrSheets[0]  = "Grid604UC_1T|모비스발주현황HOSTUP_정상";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

function btn_false_OnClick(obj)
{
	var fName   = "모비스발주현황HOSTUP__Error";
	var arrSheets  =  Array();  

	arrSheets[0]  = "Grid604UC_2T|모비스발주현황HOSTUP__Error";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setSVHE604UC"){

		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}

