	ds_SVHE604UC_MOBISE.ClearData();
	ds_SVHE604UC_0.ClearData();
	ds_SVHE604UC.ClearData();
	ds_SVHE604UC_3.ClearData();
	ds_SVHE604UC_1.ClearData();
	ds_SVHE604UC_2.ClearData();
	//ds_FILE.ClearData();//TEST용
}

