 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = '\\' + "주간소요품목제어마스타 등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|주간소요품목제어마스타 등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

