function fn_Search()
{
	getSVZE030();
}

function fn_Input()
{
	if(!validationChk()){
		return;
	}	
	if(checkInsertCount("ds_SVZE030")== false) return;
	if(ds_SVZE030.rowcount < 1) fn_Search();  
	ds_SVZE030.AddRow();
	ds_SVZE030.SetColumn(ds_SVZE030.currow, "COMPS", ComboBoxPlant.Value);
	ds_SVZE030.SetColumn(ds_SVZE030.currow, "PYVND", ed_PYVND.Text);	
}

function fn_Delete()
{
	if(ds_SVZE030.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_SVZE030.DeleteRow(ds_SVZE030.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
}

