/********************************************************************** 
 * 내용설명 : 구분 값이 0,1이 아닐경우 이동불가
 **********************************************************************/ 

function imgMaker_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_PYVND.Text);
	if (length(trim(ed_PYVND.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "B0";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_PYVND.Text = rtn[0];
		st_PYVND.Text = rtn[1];
	}
}

function ed_PYVND_OnCharChanged(obj,strPreText,strPostText)
{
	st_PYVND.Text = "";
}

function ed_PYVND_OnChanged(obj,strText)
{
	ds_SVZE030.ClearData();
}

function ed_PYVND_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_PYVND.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CUSMAS1.GetRowCount() < 1){
		alert(setPopMessage("4671"));
		setMessage("4671");	
		ed_PYVND.SetFocus();
		return;
	}
	st_PYVND.Text=ds_CUSMAS1.GetColumn(0,"CUSNM");
	http.usewaitcursor = true;	
}


function getMKPNO(tval){

	http.usewaitcursor = false;
	var sKind		= "getMKPNO";		
	var sMethodName = "service::pscomlib/selectEM000PR3.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_EM000PR=ds_EM000PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CPRTN=" + quote(tval);
		sArgument  += " CUSNO=" + quote(ds_SVZE030.GetColumn(ds_SVZE030.currow,"PYVND"));
		sArgument  += " MAKYM=" + quote("SUBSTR(trim(char(date(current date), ISO)),1,4) || SUBSTR(trim(char(date(current date), ISO)),6,2)");
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if(ds_EM000PR.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
	http.usewaitcursor = true;
}

function getDETAIL(tval){

	http.usewaitcursor = false;
	var sKind		= "getDETAIL";		
	var sMethodName = "service::pscomlib/selectEM000PR_DETAIL.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_EM000PR_DETAIL=ds_EM000PR_DETAIL";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " SELCHK=" + quote("CPRTN");
		sArgument  += " CPRTN=" + quote(tval);
		sArgument  += " CUSNO=" + quote(ds_SVZE030.GetColumn(ds_SVZE030.currow,"PYVND"));
		sArgument  += " COMPS=" + quote(ds_SVZE030.GetColumn(ds_SVZE030.currow,"COMPS"));
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if(ds_EM000PR_DETAIL.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
	http.usewaitcursor = true;
}
function ds_SVZE030_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "PYVNO"){
	http.usewaitcursor = false;
		if(varNewValue != "" ){
			if(length(varOldValue)!=0 || varOldValue != varNewValue){
				var tval=getMKPNO(toUpper(varNewValue));
				if(tval==0){
					alert(setPopMessage("4001"));  //Part Number Cross Ref MA에 미등록된 득의선,품번입니다.
					setMessage("4001");	
					//alert("Part Number Cross Ref MA에 미등록된 득의선,품번입니다.");  //Part Number Cross Ref MA에 미등록된 득의선,품번입니다.
					ds_SVZE030.SetColumn(ds_SVZE030.currow,"PYVNO", "");
					return false;
				}else{
					var tval=getDETAIL(toUpper(varNewValue));
					if(tval==0){
						alert(setPopMessage("4001"));  //Part Number Cross Ref MA에 미등록된 득의선,품번입니다.
						setMessage("4001");	
						//alert("Part Number Cross Ref MA에 미등록된 득의선,품번입니다.");  //Part Number Cross Ref MA에 미등록된 득의선,품번입니다.
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"PRTNO", "");
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"ITDSC", "");
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"CARCD", "");
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"CARNM", "");
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"CUSNM", "");
						return false;
					}
					else{
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"PYVNO", ds_EM000PR_DETAIL.GetColumn(0,"CPRTN"));
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"PRTNO", ds_EM000PR_DETAIL.GetColumn(0,"PRTNO"));
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"ITDSC", ds_EM000PR_DETAIL.GetColumn(0,"ITDSC"));
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"CARCD", ds_EM000PR_DETAIL.GetColumn(0,"CARCD"));
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"CARNM", ds_EM000PR_DETAIL.GetColumn(0,"CARNM"));
						ds_SVZE030.SetColumn(ds_SVZE030.currow,"CUSNM", ds_EM000PR_DETAIL.GetColumn(0,"CUSNM"));					
					}
				}
			}
		}
		if(varNewValue == "" ){
			ds_SVZE030.SetColumn(ds_SVZE030.currow,"PYVNO", "");
			ds_SVZE030.SetColumn(ds_SVZE030.currow,"PRTNO", "");
			ds_SVZE030.SetColumn(ds_SVZE030.currow,"ITDSC", "");
			ds_SVZE030.SetColumn(ds_SVZE030.currow,"CARCD", "");
			ds_SVZE030.SetColumn(ds_SVZE030.currow,"CARNM", "");
			ds_SVZE030.SetColumn(ds_SVZE030.currow,"CUSNM", "");
		}			
	http.usewaitcursor = true;
	}	
}
