/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = '\\' + "업체부품구성마스타조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|업체부품구성마스타조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

