var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1.3"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	//searchBar.Width = G_ds_design.GetColumn(0,"Width");
	//searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getSVZE511P(){

	if(!validationChk()){
		return;
	}	

	if(length(ed_SPYV.Text)==0){
		ed_SUBSPYV.Text="00000000";
	}
	else{
		ed_SUBSPYV.Text=ed_SPYV.Text;
	}	
	if(length(ed_EPYV.Text)==0){
		ed_SUBEPYV.Text="99999999";
	}
	else{
		ed_SUBEPYV.Text=ed_EPYV.Text;
	}
	if(length(ed_SCAR.Text)==0){
		ed_SUBSCAR.Text="AAA";
	}
	else{
		ed_SUBSCAR.Text=ed_SCAR.Text;
	}	
	if(length(ed_ECAR.Text)==0){
		ed_SUBECAR.Text="ZZZ";
	}
	else{
		ed_SUBECAR.Text=ed_ECAR.Text;
	}

	var eDateVal= addMonth(cmb_SDATE.Value,-1);
	var sKind		= "getSVZE511P";						
	var sMethodName = "service::pssallib/selectSVZE511P.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SVZE511=ds_SVZE511";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@SPYV=" + quote(ed_SUBSPYV.Text); //업체s
		sArgument += "@EPYV=" + quote(ed_SUBEPYV.Text); //업체e
		sArgument += "@SCAR=" + quote(ed_SUBSCAR.Text); //차종s
		sArgument += "@ECAR=" + quote(ed_SUBECAR.Text); //차종e
		sArgument += "@SDATE=" + quote(substr(cmb_SDATE.Value,0,6)); //선택년월
		sArgument += "@EDATE=" + quote(substr(eDateVal,0,6)); //선택년월
		sArgument += "@PCHK=" + quote(cmb_PCHK.Value); //상각구분
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	//Ubi0.setJrfFileDir("http://10.74.139.50/ui/PSSALLIB/REPORT/");
	//Ubi0.setJrfFileDir("http://10.72.209.23/ui/PSSALLIB/REPORT/");
	//Ubi0.setJrfFileDir("http://dione.dnpe.co.kr/ui/PSSALLIB/REPORT/");
	Ubi0.setJrfFileDir(G_baseURL+"ui/PSSALLIB/REPORT/");
	Ubi0.setJrfFileName("svze511_rep.jrf");
	Ubi0.SetDataset("ds_SVZE511", "ds_SVZE511");	
	Ubi0.SetArgument("SCOMPS",ComboBoxPlant.Value);
	Ubi0.SetArgument("SPYV",ed_SUBSPYV.Text);
	Ubi0.SetArgument("EPYV",ed_SUBEPYV.Text);
	Ubi0.SetArgument("SCAR",ed_SUBSCAR.Text);
	Ubi0.SetArgument("ECAR",ed_SUBECAR.Text);
	Ubi0.SetArgument("SDATE",cmb_SDATE.Value);
	Ubi0.SetArgument("PCHK",cmb_PCHK.Text);
	Ubi0.Retrieve();
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var sdVal = cmb_SDATE.Value; //선택년월
	var scVal = cmb_PCHK.Value;	//상각구분
	if (sdVal == "")
	{
		alert(setPopMessage("3048"));
		result = false;
	}
	if (scVal == "")
	{
		alert(setPopMessage("3048"));
		result = false;
	}
	return result;
}

