function setExcel()
{
	var fName   = "월업체별차종별개발비상각 현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월업체별차종별개발비상각 현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

