/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSZZE100(){
	if(length(trim(ed_SLPNO.Text)) < 1){
		alert(setPopMessage("4004"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("4004");	
		ed_SLPNO.SetFocus();
		return;
	}

	//조회
	var sKind		= "getSZZE100";						
	var sMethodName = "service::pssallib/selectSZZE100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SZZE100=ds_SZZE100";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@SLPNO=" + quote(ed_SLPNO.Text);
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		ca_MOVDP.Value = ds_SZZE100.GetColumn(0,"MOVDPS");
		ed_HOUSE.Value = ds_SZZE100.GetColumn(0,"HOUSES");
		ed_REASN.value = ds_SZZE100.GetColumn(0,"REASNS");
		if(ds_SZZE100.GetColumn(0,"ISSDPS") == "0"){
			ed_ISSDP.value = "";
		}else{
			ed_ISSDP.value = ds_SZZE100.GetColumn(0,"ISSDPS");
		}
		if(ds_SZZE100.GetColumn(0,"RTNDPS") == "0"){
			ed_RTNDP.Value = "";
		}else{
			ed_RTNDP.Value = ds_SZZE100.GetColumn(0,"RTNDPS");
		}
		ca_UMSNG.Value = ds_SZZE100.GetColumn(0,"UMSNGS");
		ca_TRNDT.Value = ds_SZZE100.GetColumn(0,"TRNDTS");
		ca_INOUT.Value = ds_SZZE100.GetColumn(0,"PRSDPS");
		if(ca_INOUT.Value != "1"){
			Static19.Visible = true;
			ca_UMSNG.Visible = true;
			Static18.Visible = true;
		}else{
			Static19.Visible = false;
			ca_UMSNG.Visible = false;
			Static18.Visible = false;
		}
		ds_SZZE100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
		if(ds_SZZE100.GetColumn(0,"UPTCGM") == "Y"){
			st_UPTCGM.Text = "Y";
			alert("이미CIGMA에 갱신된 SLIP NO입니다. 조회만 가능합니다.");
		}else{
			st_UPTCGM.Text = "";
			var count_t = 15-ds_SZZE100.rowcount;
			for (var j=0; j<count_t;j++){
				ds_SZZE100.AddRow();
				ds_SZZE100.SetColumn(ds_SZZE100.Row, "COMPSS", cb_searchCodeName1.Value); // default값 설정
				ds_SZZE100.SetColumn(ds_SZZE100.Row, "PRSDPS", ca_INOUT.Value);
				ds_SZZE100.SetColumn(ds_SZZE100.Row, "SLPNOS", ed_SLPNO.Text);
				ds_SZZE100.SetColumn(ds_SZZE100.Row, "HOUSES", ed_HOUSE.Text);
				if(length(ed_ISSDP.Text) > 0){
					ds_SZZE100.SetColumn(ds_SZZE100.Row, "ISSDPS", ed_ISSDP.Text);
				}else{
					ds_SZZE100.SetColumn(ds_SZZE100.Row, "ISSDPS", "0");
				}
				if(length(ed_RTNDP.Text) > 0){
					ds_SZZE100.SetColumn(ds_SZZE100.Row, "RTNDPS", ed_RTNDP.Text);
				}else{
					ds_SZZE100.SetColumn(ds_SZZE100.Row, "RTNDPS", "0");
				}
				ds_SZZE100.SetColumn(ds_SZZE100.Row, "REASNS", ed_REASN.Text);
				ds_SZZE100.SetColumn(ds_SZZE100.Row, "MOVDPS", ca_MOVDP.Value);
				ds_SZZE100.SetColumn(ds_SZZE100.Row, "TRNDTS", ca_TRNDT.Value);
				if (ca_INOUT.Value == "1"){  //영업이면
					ds_SZZE100.SetColumn(ds_SZZE100.Row, "UMSNGS", " ");
				}else{
					ds_SZZE100.SetColumn(ds_SZZE100.Row, "UMSNGS", ca_UMSNG.Value);
				}
			}
		}
		gridFormChange();
		ds_SZZE100.FireEvent=true;  
		alert(setPopMessage("1004"));
		setMessage("1004");
		MainGrid.SetFocus();
		ds_SZZE100.rowposition = 0;
		MainGrid.SetCellPos(3);
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //   [알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setSZZE100(){
	if(length(trim(ed_SLPNO.Text)) < 1){
		alert("SLIP NO를 확인바랍니다.");
		ed_SLPNO.SetFocus();
		return;
	}
	if (st_UPTCGM.Text == "Y"){
		alert("이미CIGMA에 갱신된 SLIP NO입니다. 조회만 가능합니다.");
		return;
	}
	if(length(trim(ed_HOUSE.Text)) < 1){
		alert("HOUSE를 확인바랍니다.");
		ed_HOUSE.SetFocus();
		return;
	}
	if(!(ca_INOUT.Value == "1" || ca_INOUT.Value == "2" || ca_INOUT.Value == "3")){
		alert("관리부서 확인바랍니다.");
		ca_INOUT.SetFocus();
		return;
	}
	if(!(ed_Action.Text=="A" || ed_Action.Text=="Q")){
		alert("활동CD를 확인바랍니다.");
		ed_Action.SetFocus();
		return;
	}
	ds_SZZE100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
	for (var j=0; j<ds_SZZE100.rowcount;j++){
		if(length(trim(ds_SZZE100.GetColumn(j,"PRTNOS"))) > 0){
			if(ds_SZZE100.GetColumn(j,"TRQTYS") == null){
				alert("QUANTITY(수량)을 확인바랍니다.");
				ds_SZZE100.Row =j;
				MainGrid.SetCellPos(3);
				return;
			}
			if(ca_INOUT.Value != "1"){
				if(ds_SZZE100.GetColumn(j,"UMCSTS") == null){
					alert("COST(단가) 확인바랍니다.");
					ds_SZZE100.Row =j;
					MainGrid.SetCellPos(6);
					return;
				}
			}else{
				ds_SZZE100.SetColumn(j, "UMCSTS", "0");
			}
			ds_SZZE100.SetColumn(j, "CFMQYS", ds_SZZE100.GetColumn(j,"TRQTYS")); //확인　수량
			ds_SZZE100.SetColumn(j, "CFMCDS", "Y"); //확인　유무
			ds_SZZE100.SetColumn(j, "CFMDAT", nowDate); //확인　일자
			ds_SZZE100.SetColumn(j, "CFMTIM", left(time(),6)); //확인　시간
			ds_SZZE100.SetColumn(j, "CFMMAN", userID); //확인처리자
			ds_SZZE100.SetColumn(j, "CFMDSP", "DIONE"); //확인단말기
		}
	}
	ds_SZZE100.FireEvent=true;  

 	http.Sync = true;
	var sKind		= "SAVE_SZZE100";						
	var sMethodName = "service::pssallib/insertSZZE100.do";
	var sInDataSet  = "ds_SZZE100=ds_SZZE100:U";
	var sOutDataSet = "ds_SZZE100_CHECK=ds_SZZE100_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

 }

