/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "SAVE_SZZE100"){
		if (ds_SZZE100_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_SZZE100_CHECK.GetRowCount();i++){		
				alert(ds_SZZE100_CHECK.GetColumn(i,"SLPNOS") + " , "
					+ ds_SZZE100_CHECK.GetColumn(i,"PRTNOS") + " = "
					+ ds_SZZE100_CHECK.GetColumn(i,"RTMSG"));	
			}
			return;		
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}
}

