function fn_Input()
{
	if(!checkP()) return ;
	st_UPTCGM.Text = "";
	ds_SZZE100.ClearData();
	//if(checkInsertCount("ds_SZZE100")== false) return;
	ds_SZZE100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
	for (var i=0; i<15; i++){
		ds_SZZE100.AddRow(i);
		ds_SZZE100.SetColumn(ds_SZZE100.Row, "COMPSS", cb_searchCodeName1.Value); // default값 설정
		ds_SZZE100.SetColumn(ds_SZZE100.Row, "PRSDPS", ca_INOUT.Value);
		ds_SZZE100.SetColumn(ds_SZZE100.Row, "SLPNOS", ed_SLPNO.Text);
		ds_SZZE100.SetColumn(ds_SZZE100.Row, "HOUSES", ed_HOUSE.Text);
		if(length(ed_ISSDP.Text) > 0){
			ds_SZZE100.SetColumn(ds_SZZE100.Row, "ISSDPS", ed_ISSDP.Text);
		}else{
			ds_SZZE100.SetColumn(ds_SZZE100.Row, "ISSDPS", "0");
		}
		if(length(ed_RTNDP.Text) > 0){
			ds_SZZE100.SetColumn(ds_SZZE100.Row, "RTNDPS", ed_RTNDP.Text);
		}else{
			ds_SZZE100.SetColumn(ds_SZZE100.Row, "RTNDPS", "0");
		}
		ds_SZZE100.SetColumn(ds_SZZE100.Row, "REASNS", ed_REASN.Text);
		ds_SZZE100.SetColumn(ds_SZZE100.Row, "MOVDPS", ca_MOVDP.Value);
		ds_SZZE100.SetColumn(ds_SZZE100.Row, "TRNDTS", ca_TRNDT.Value);
		if (ca_INOUT.Value == "1"){  //영업이면
			ds_SZZE100.SetColumn(ds_SZZE100.Row, "UMSNGS", " ");
		}else{
			ds_SZZE100.SetColumn(ds_SZZE100.Row, "UMSNGS", ca_UMSNG.Value);
		}
		if(ed_ISSDP.Value == ""){
			ds_SZZE100.SetColumn(ds_SZZE100.Row, "QTYCDS", "1");
		}else{
			ds_SZZE100.SetColumn(ds_SZZE100.Row, "QTYCDS", "2");
		}
		//ds_SZZE100.SetColumn(ds_SZZE100.Row, "SHPTO", "0"); // default값 설정
		//ds_SZZE100.SetColumn(ds_SZZE100.Row, "INOUT", ca_INOUT.Value); // default값 설정

	}
	gridFormChange();
	//ds_SZZE100.Row =0;
	MainGrid.SetFocus();
	ds_SZZE100.rowposition = 0;
	MainGrid.SetCellPos(1);
	ds_SZZE100.FireEvent=true;
	ed_Action.Text="A";
}

function fn_Delete()
{
	if (st_UPTCGM.Text == "Y"){
		alert("이미CIGMA에 갱신된 SLIP NO입니다. 조회만 가능합니다.");
		return;
	}
	if(ds_SZZE100.rowcount < 1){
		alert("삭제할 내역이 없습니다. 확인바랍니다.");
		return;
	}
	ds_SZZE100.DeleteRow(ds_SZZE100.currow);

	alert(setPopMessage("1011"));     //[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.
	setMessage("1011");
	ed_Action.Text="Q";
}

