function ed_REASN_OnChanged(obj,strText)
{
	ds_SZZE100.ClearData();
	if(!(strText == "11" || strText == "12" || strText == "13" || strText == "14" || strText == "21" || strText == "22" || strText == "29" || strText == "99")){
		alert("REASON CODE를 확인하세요");
		obj.Text = "";
		obj.SetFocus();
	}
}

function ed_DP_OnChanged(obj,strText)
{
	ds_SZZE100.ClearData();
	http.usewaitcursor = false;  //transaction호출후 버튼이벤트가 발생되지 않아서...
	http.Sync 	= true;
	var sKind		= "getMST652LF";		
	var sMethodName = "service::pscomlib/selectMST652LF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST652LF=ds_MST652LF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument  += " DPCDCM=" + quote(strText);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if(ds_MST652LF.rowcount < 1){
		alert("부서코드를 확인바랍니다.");
		obj.Text = "";
		obj.SetFocus();
	}
	http.usewaitcursor = true; 
}

function ca_MOVDP_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam)
{
	trace(nChar);
	if (nChar == 13) fn_Input();
}

function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1003")); setMessage("1003");
		}
	}
}

function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		if(confirm("저장하시겠습니까? ") == false) return;
		fn_Save();
	}
}
