/********************************************************************** 
 * 내용설명 : csv파일로 저장
 **********************************************************************/ 
function setExcel()
{
/*
	var fName   = "SCRAP TICKET처리현황";
	var arrSheets  =  Array();  
	arrSheets[0]  = "MainGrid|SCRAP TICKET처리현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
*/
	var strCsvContents = MainGrid.GetCSVData();
	FileDialog0.Type = "SAVE";
	//FileDialog0.FilePath = "C:\\";
	FileDialog0.FileName = "SLPNO_"+ed_SLPNOS+".csv";
	FileDialog0.Open("C:\\");

	File.FileName = FileDialog0.FilePath + "\\" + FileDialog0.FileName;
	File.Open ( "w+" );
	var len = File.Write ( strCsvContents , -1 );
//alert ( "파일에 쓴 Buffer의 Size = " + len );
	File.Close();
}

