function getSZZE110C(){
	if(length(trim(ed_SLPNO.Text)) < 1){
		alert(setPopMessage("4004"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("4004");	
		ed_SLPNO.SetFocus();
		return;
	}

	//조회
	var sKind		= "getSZZE110C";						
	var sMethodName = "service::pssallib/selectSZZE100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SZZE100=ds_SZZE100";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	http.Sync = true;
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " SLPNO=" + quote(ed_SLPNO.Text);
	//var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if(ds_SZZE100.rowcount > 0){ //정상조회
		if(ds_SZZE100.GetColumn(0,"UPTCGM") == "Y"){
			st_UPTCGM.Text = "Y";
			ds_SZZE100.ClearData();
			alert(setPopMessage("4710"));  //   [확인]이미 CIGMA에 갱신된 SLIP NO.입니다.
			setMessage("4710");	
			confirm_false();
			ed_SLPNO.SetFocus();
			return;
		}else if(ds_SZZE100.GetColumn(0,"CFMCDS") == " "){
			ds_SZZE100.ClearData();
			alert(setPopMessage("4711"));  //   [확인]SLIP NO. 확인이 안되었습니다.
			setMessage("4711");
			confirm_false();
			ed_SLPNO.SetFocus();
			return;		
		}else if(ds_SZZE100.GetColumn(0,"UMSNGS") == "1"){
			ds_SZZE100.ClearData();
			alert(setPopMessage("4712"));  //  [확인]유상처리된 품번은 정상출고 처리 하세요!
			setMessage("4712");	
			confirm_false();
			ed_SLPNO.SetFocus();
			return;	
		}else{
			confirm_true();
			cmb_YN.Index=0;
			alert(setPopMessage("1004"));
			setMessage("1004");	
			cmb_YN.SetFocus();
		}
	}else {   //data값이 없을때
		alert(setPopMessage("4713"));  //   [확인]미등록돤 SLIP NO.입니다.
		setMessage("4713");	
		confirm_false();
		ed_SLPNO.SetFocus();
	}
}

