/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
		return;
	}else if (svcId = "SAVE_SZZE110C"){
		//사업부별 공통ID(as400 USERID)로 실행
		var iDget = getRemotecallID(comps);
		if (iDget != "ERROR"){
			callUser = iDget;
			callPass = iDget;
		}else{
			alert("사용자error => TX에 저장은 되었지만 CIGMA이관은 실패... 정보관리팀으로 연락바랍니다.");
			alert(setPopMessage("1444"));
			setMessage("1444");	
			confirm_false();
			return;
		}

		//리모트콜 실행(SZZE110rc용)
		var param = cb_searchCodeName1.Value + right('          '+ID3,10) + right('          '+devID,10);
		for(i=length(param)+1;i<=40;i++){
			param += " ";
		}
		param += callUser;		
		for(j=1;j<=10-length(callUser);j++){
			param += " ";
		}
		param += '000';

		SetWaitCursor(true);
		SetCapture();

		WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

		// 연결 (접속아이피 , 아이디, 비밀번호)	
		var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

		//접속성공시 
		if(rtn != 0){
			//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
			var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
			//정상적으로 수행했을 시
			if(rtn2 != 0){
				ReleaseCapture();
				SetWaitCursor(false);
				var rParm =substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3);
				if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
					WTA_RemoteCall0.RC_Disconnect();	//연결종료
					alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
					setMessage("1001");	
					confirm_false();
					ds_SZZE100.ClearData();
					ds_SZZE110.ClearData();
					ed_SLPNO.SetFocus();
				}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
					alert(WTA_RemoteCall0.RC_GetReturnParm(1));
					WTA_RemoteCall0.RC_Disconnect();	//연결종료
					alert("err = "+rParm+" =>TX에 저장은 되었지만 CIGMA이관은 실패... 정보관리팀으로 연락바랍니다.");
					alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
					confirm_false();
					setMessage("1002");	
					return false;
				}else {
					alert(WTA_RemoteCall0.RC_GetReturnParm(1));
					WTA_RemoteCall0.RC_Disconnect();	//연결종료
					alert("err = "+rParm+" =>TX에 저장은 되었지만 CIGMA이관은 실패... 정보관리팀으로 연락바랍니다.");
					alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
					setMessage("1015");	
					confirm_false();
					return false;
				}
			}else{
				var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				ReleaseCapture();
				SetWaitCursor(false);
				//오류 코드 경고창
				alert("pgm error" + " =>TX에 저장은 되었지만 CIGMA이관은 실패... 정보관리팀으로 연락바랍니다.");
				alert("에러 메세지=======>" + bstrErrorMessage);
				confirm_false();
			}
		//접속실패시
		}else{
			var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("접속 error" + " =>TX에 저장은 되었지만 CIGMA이관은 실패... 정보관리팀으로 연락바랍니다.");
			alert("접속 실패 오류=======>" + ulErrorCode);
			confirm_false();
		}
	}

}

