function cmb_YN_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		if(obj.Value != "Y") return;
		if(confirm("이관하시겠습니까? ") == false) return;
		fn_Save();
	}
}

function confirm_false()
{
	Static13.Visible = false;
	Static14.Visible =false;
	Static17.Visible = false;
	cmb_YN.Visible = false;
	MainGrid.Visible = false;
}
function confirm_true()
{
	Static13.Visible = true;
	Static14.Visible =true;
	Static17.Visible = true;
	cmb_YN.Visible = true;
	MainGrid.Visible = true;
}
