function Button1_OnClick(obj)
{
	var pos_x = 20;
	var comps 	= object("Tab_1.Tab"+Tab_1.TabIndex).Components["Button"];	
	var cnt 	;
	if(comps==null){
		cnt 	= pos_x;
	}else{
		cnt 	= toString(pos_x*(comps.count()+1));
	}

	var str 	= "left='"+cnt+"' top='"+cnt+"' width='100' height='25' text='Image_"+toString(cnt)+"' onclick='btnClick'";

	object("Tab_1.Tab"+Tab_1.TabIndex).Create("Button", "button_"+toString(cnt) , str );
}


function Button3_OnClick(obj)
{
	createComponent( obj
					, "Button"
					, "button_tab"
					, 'left="10" top="330" width="120" height="40" text="subTab Add" onclick="btnAddTab"');

	createComponent( obj
					, "Tab"
					, "Tab_1"
					, 'left="150" top="250" width="550" height="150" OnChanged="btnClickTab"');
	btnAddTab();
	Button1.Enable = true;
	Button3.Enable = false;
}

function btnAddTab()
{
	var new_id = "Tab"+toString(Tab_1.TabCount);
	Tab_1.InsertTab(new_id);
	object("Tab_1."+new_id).text = new_id;
}

function btnClickTab(obj,nOldIndex,nNewindex){
	var idx = Tab_1.TabIndex  ;
}

function createComponent( obj, strCompName,strCompID,strAttrib){

	switch(obj.UserData){
		case "Button":
			Create(strCompName, strCompID, strAttrib);
			break;
		case "Image":
			Create(strCompName, strCompID, strAttrib);
			break;

		case "Tab":
			Create(strCompName, strCompID, strAttrib);
			break;
	}
}

