function on_LoadCompleted(obj){
	getTitle();	
	getComCode();
	getMst040();
	Div0.st_beforYM.Text = substr(ds_MST040PF.GetColumn(0,"MJINT"),0,4)+"-"+substr(ds_MST040PF.GetColumn(0,"MJINT"),4,2);
	Div0.cal_PLNYM.Value = next_month;
	var mm = substr(Div0.cal_PLNYM.Value,4,2);
	Div0.ed_gu.Text = iif(mm==04 || mm==10,"2","1");
	gFcOnTopButtonColor("0"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 		

	//st_Msg.Visible = false;
	Div0.cal_PLNYM.SetFocus();
}

