	if (comps == "E1"){
		Div0.cb_searchCodeName1.Value="PE";
	}else{
		Div0.cb_searchCodeName1.Value="PS";
	}
}

function getMst040()
{
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("FYS100PF");
	http.Sync 		= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 		= false;
	next_month = AddMonth(ds_MST040PF.GetColumn(0,"MJINT")+"01",1);
}
function setMst040()
{
	var sKind		= "setmst040";						
	//var sMethodName = "service::pscomlib/updateMST040_PSDIOLIB.do";     		// 서버에서 호출 될 Method 명
	var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div0.cb_searchCodeName1.Value);
	sArgument += " MAJCD=" + quote("FYS100PF");
	sArgument += " MJINT=" + quote(substr(Div0.cal_PLNYM.Value,0,6));
	sArgument += " MAJMK=" + quote("DIONE "+G_ds_user.GetColumn(0,"USER_ID"));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

/********************************************************************** 
 * 내용설명 : 마감
 * 주요변수 : 
 **********************************************************************/ 
function setFYSC100C(){
	if(length(trim(Div0.cb_searchCodeName1.Value)) < 1){
		alert(setPopMessage("1006"));  //[알림] 지원하지 않는 시스템 입니다. 정보관리팀으로 문의하십시요.
		setMessage("1006");	
		return;
	}
	if(length(trim(Div0.cal_PLNYM.Value)) < 8){
		alert(setPopMessage("5010"));  //[확인] 산출년월을 확인바랍니다.
		setMessage("5010");	
		Div0.cal_PLNYM.SetFocus();
		return;
	}
	if(!(Div0.ed_gu.Value == "1" || Div0.ed_gu.Value == "2")) {
		alert(setPopMessage("5009"));  // [확인] 이월구분을 확인바랍니다.
		setMessage("5009");	
		Div0.ed_gu.SetFocus();
		return;
	}
	var mm = substr(Div0.cal_PLNYM.Value,4,2);
	if(mm==04 || mm==10){
		if(Div0.ed_gu.Value != "2"){
			alert(setPopMessage("5011"));  // [경고] 04월,10월은 '2'실사재고를, 그외 월은 '1'전월재고를 입력바랍니다.
			setMessage("5011");	
			Div0.ed_gu.SetFocus();
			return;
		}
	}else{
		if(Div0.ed_gu.Value != "1"){
			alert(setPopMessage("5011"));  // [경고] 04월,10월은 '2'실사재고를, 그외 월은 '1'전월재고를 입력바랍니다.
			setMessage("5011");	
			Div0.ed_gu.SetFocus();
			return;
		}
	}
	if(comps == "E1"){
		if(Div0.cb_searchCodeName1.Value != "PE"){
			alert(setPopMessage("1510"));  // [경고] 사업부 구분을 확인바랍니다.
			setMessage("1510");	
			Div0.ed_gu.SetFocus();
			return;
		}
	}else{
		if(Div0.cb_searchCodeName1.Value != "PS"){
			alert(setPopMessage("1510"));  // [경고] 사업부 구분을 확인바랍니다.
			setMessage("1510");	
			Div0.ed_gu.SetFocus();
			return;
		}
	}
	getMst040();
	var magYM = substr(Div0.cal_PLNYM.Value,0,6);
	if(magYM != substr(next_month,0,6)){
		alert(setPopMessage("5012"));  //[경고] 전월에 산출작업이 미완료 되었거나 이미 산출된 년월입니다.
		setMessage("5012");	
		Div0.cal_PLNYM.SetFocus();
		return;
	}
	if(confirm(substr(Div0.cal_PLNYM.Value,0,4)+"년 "+substr(Div0.cal_PLNYM.Value,4,2)+"월 부품수율 소요량 산출 작업입니다. 작업하시겠습니까? ") == false) return;
	var befYM = substr(AddMonth(Div0.cal_PLNYM.Value,-1),0,6); //전월
	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	//callUser = ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	//callPass = ds_ssoaccount.GetColumn(0,"SYSTEM_PW");

	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}

	//리모트콜 실행
	param = Div0.cb_searchCodeName1.Value + substr(Div0.cal_PLNYM.Value,0,6) + Div0.ed_gu.Value + befYM;
	for(i=length(param)+1;i<=40;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	st_Msg.Visible = true;
	Div0.Enable = false;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			st_Msg.Visible = false;
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				//setMst040(); //마감관리마스타update
				alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
				setMessage("1001");	
				getMst040();
				Div0.st_beforYM.Text = substr(ds_MST040PF.GetColumn(0,"MJINT"),0,4)+"-"+substr(ds_MST040PF.GetColumn(0,"MJINT"),4,2);
			} else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			} else {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(rtnCode);
		if (rtnCode == 0){
			alert(setPopMessage("1001"));
			setMessage("1001");
		}else if (rtnCode == 1){
			alert("타사용자가 사용중입니다. 확인바랍니다.");
			//setMessage("1002");
		}else{
			alert(setPopMessage("1002"));
			setMessage("1002");
		}
	}
}

