/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getOutSystem(){
	http.Sync = true;
	var sKind		= "getOutSystem";						
	var sMethodName = "service::user/selectOutSystem.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_outSystem=ds_outSystem";    			// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument   += " INOUT_GUBN='O'";
		sArgument   += " COMP=" + quote(cb_comp.Value);
		// sArgument   += " PLNT=" + quote(cb_PLNT.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	ds_outSystem_tmp.Copy("ds_outSystem");

}


/********************************************************************** 
 * 개 발 자 : 황규철 
 * 개발일자 : 2010-11-26
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_EXSC=ds_ComCode_EXSC";
	var sArgument   = "";
		sArgument   += " CLASS='EXSC'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_COMP=ds_ComCode_COMP";
	var sArgument   = "";
		sArgument   += " CLASS='COMP'";
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	

	//사업부
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_COMP1=ds_ComCode_COMP";
	var sArgument   = "";
		sArgument   += " CLASS='COMP'";
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
	http.Sync = false;
	ds_ComCode_COMP1.InsertRow(0);
	ds_ComCode_COMP1.SetColumn(0,"CODE1","");
	ds_ComCode_COMP1.SetColumn(0,"CODE_NAME","[전체]");
	cb_comp.index = 0;		

	//사업장
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT1=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	// ds_ComCode_PLNT1.InsertRow(0);
	// ds_ComCode_PLNT1.SetColumn(0,"CODE1","");
	// ds_ComCode_PLNT1.SetColumn(0,"CODE_NAME","[전체]");
	// cb_PLNT.index = 0;
}

